import React, { Component } from 'react'
import {Link, withRouter} from 'react-router-dom';
import './EmployeAllocation.css';
import axios from 'axios';
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
export class EditEmplalloc extends Component {
    constructor(props)
    {
        super(props)
        this.state = {
            data :[],
                userdata : [],
                locationdata : [],
                id : this.props.match.params.id,
                location_id : "",
                zone_id : "",
                location_name: "",
                zone_name : "",
                employe : ""


           };
          //  this.onSubmit = this.onSubmit.bind(this);
          this.change = this.change.bind(this);
    }
    componentDidMount ()
    {
      this.handleClick();
    }
    handleClick () {
      let Editid = this.props.match.params.id;
      console.log(this.props.match.params.id)
        // // let meetupId = items.admin_id;
        // console.log(this.props)
        //     // console.log(meetupId);
        //     let sitemeet = item.admin_id;
      axios.get(`${API_URL}emp_loc/detail?id=${Editid}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.data.emp_locdata)
            // this.setState({ data:response.data})
            // this.parseJSON(this.state)
            this.setState({employe:response.data.emp_locdata[0].employe_name})
            this.setState({employe_id:response.data.emp_locdata[0].employe_id})
            this.setState({zone_name:response.data.emp_locdata[0].zone_name})
            this.setState({location_name:response.data.emp_locdata[0].location_name})
            this.setState({zone_id:response.data.emp_locdata[0].zone_id})
            this.setState({location_id:response.data.emp_locdata[0].location_id})
          })
          .catch(error => {
            console.log(error.response);
          })



      axios.get(`${API_URL}list/zonelist`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.data.zonedata)
            this.setState({ userdata:response.data.zonedata})
            // this.parseJSON(this.state)
          })
          .catch(error => {
            console.log(error.response);
          })

      }

      locationclick = e =>  
    {
      e.preventDefault();
      console.log(this.state.zone_id);
      // console.log(this.state.value);
      const sitemeet = this.state.zone_id;
      axios.get(`${API_URL}list/locationonzone?zone_id=`+sitemeet,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.data.locationdata)
            this.setState({ locationdata:response.data.locationdata})
          })
          .catch(error => {
            console.log(error.response);
          })

    }


      change  = e => {
        this.setState ({
          [e.target.name]: e.target.value
        });
      };
      getWebsite = () =>
      {
          fetch('/');
      }
      onSubmit = e =>
      {
          e.preventDefault();
          console.log(this.state);
          this.setState ({
            id : this.props.match.params.id,
          })
          axios.post(`${API_URL}emp_loc/update`,this.state,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
        .then(function(response){
          console.log(response)
             if(response.data.success){
            //   console.log(json);
            alert(response.data.msg);
            this.props.history.push('/ViewEmployeAllocation')
        }
        else{
          alert(response.data.msg)
      }
    })
    .catch(error => {
      console.log(error.response);
    })
      };
  render() {
    // const editmeet = this.props.editmeet;
    return (
        <div className="skin-blue fixed-layout">
        <div className="page-wrapper">

             <div className="container-fluid">

               <div className="row page-titles">
                   <div className="col-md-5 align-self-center">
                       {/* <h4 className="text-themecolor">Forms</h4> */}
                       <nav aria-label="breadcrumb">
                            <ol class="breadcrumb">
                                <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                                <li className="breadcrumb-item active" aria-current="page">Edit Allocated Employe</li>
                            </ol>
                            </nav>
                   </div>

               </div>


            </div>

    {/*Form content begin */}

  <div className = "product-form-upper">
    <div className = "container">
    <div className = "below-custom-form">
        <div className = "row">
           <div className = "col-lg-12 col-md-12 col-sm-12 col-xs-12">
                   <h3>Edit Allocated Employee</h3>
           </div>
        </div>


        <form className="custom-content-form" autoComplete = 'no'>
  <div class="form-row">
  <div class="form-group col-md-4">
    <label for="inputSubcategory">Enter Zone</label>
    <select className="form-control" value={this.state.zone_id} name="zone_id"  onClick={e => this.locationclick(e)}  onChange={(e) => this.setState({zone_id: e.target.value})}>
   <option>choose....</option>
    {
              this.state.userdata ?
              this.state.userdata.map(function(item, id) {
                return(

        <option key ={id} value={item.zone_id}>{item.zone}</option>
      )
              }
      )
              :
              <span>Data is loading....</span>
            }

    </select>

    <p>You have chosenn the value : <strong>{this.state.zone_name}</strong>. Please choose again the value to EDIT.</p>
    </div>
    <div class="form-group col-md-4">
    <label for="inputSubcategory">Enter Location</label>
    <select className="form-control" value={this.state.location_id} name="location_id" onChange={(e) => this.setState({location_id: e.target.value})}>
   <option>{this.state.location_name || 'All Location'}</option>
    {
              this.state.locationdata ?
              this.state.locationdata.map(function(item, id) {
                return(

        <option key ={id} value={item.location_id}>{item.location_name || 'No Location'}</option>
      )
              }
      )
              :
              <span>Data is loading....</span>
            }

    </select>
     <p><strong>{this.state.location_name}</strong>. Please choose again the value to EDIT.</p>
    </div>
    <div className="form-group col-md-4">
           <label htmlFor="inputname">Employe Name</label>
           <input type="text" className="form-control" name="employe" value={this.state.employe} disabled />   
    </div>
  </div>

  <button class="btn btn-primary" onClick = {e => this.onSubmit(e)}>Submit</button>
  </form>


  {/* <table className="table table-hover table-bordered ">
      <thead className="bg-info custom-thead-color">
        <tr>
          <th scope="col">Full Name</th>
          <th scope="col">Admin Name</th>
          <th scope="col">Email</th>
        </tr>
      </thead>
      <tbody>
      {
              this.state.data ?
              this.state.data.map(function(item, id) {
                return(

        <tr key = {id}>
          <th scope="row">{item.admin_id} '------'  {item.full_name}</th>
          <td>{item.adminname}</td> 
          <td>{item.email}</td> 
        </tr>
      )
              }, this
      )
              :
              <span>Data is loading....</span>
            }
      </tbody>
    </table> */}


    </div>
    </div>
  </div>






        </div>
        </div>

    )
  }
  }
export default withRouter(EditEmplalloc)
