import React, { Component } from 'react';
import './Employee.css';
import {Link, withRouter} from 'react-router-dom';
import axios from 'axios';
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
class EditEmploye extends Component {
    constructor(props)
    {
        super(props)
        this.state = {
            id : this.props.match.params.id,
                employe_name : '',
                position: "",
               email : "",
               mobile1 : "",
               mobile2 : "",
               emergency_contact_number : "",
               account_detail : "",
               blood_group : ""
                // userdata : []



           };
           this.onSubmit = this.onSubmit.bind(this);
    }

    componentDidMount ()
    {
      this.handleClick();

    }
    handleClick () {
      let Editid = this.props.match.params.id;
      console.log(this.props.match.params.id)
        // // let meetupId = items.admin_id;
        // console.log(this.props)
        //     // console.log(meetupId);
        //     let sitemeet = item.admin_id;
      axios.get(`${API_URL}employe/detail?id=${Editid}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.employedata)
            // this.setState({ data:response.employedata})
            // this.parseJSON(this.state)
            this.setState({employe_name:response.data.employedata[0].employe_name})
            this.setState({email:response.data.employedata[0].employe_email})
            this.setState({mobile1:response.data.employedata[0].employe_mobile1})
            this.setState({mobile2:response.data.employedata[0].employe_mobile2})
            this.setState({emergency_contact_number:response.data.employedata[0].employe_emergency_contact_number})
            this.setState({account_detail:response.data.employedata[0].employe_account_detail})
            this.setState({position:response.data.employedata[0].employe_position})
            this.setState({blood_group:response.data.employedata[0].employe_blood_group})
          })

      }

    //   componentWillMount ()
    //   {
    //     this.positionClick();
    //   }
    //   positionClick () {
    //     fetch('employe/view',{
    //       method : 'GET',
    //       headers : {
    //         'Authorization': 'Bearer ' + this.state.token,
    //         "Content-Type" : "application/json"
    //     }
    //     })
    //       .then((res) => {
    //         res.json().then((response) => { 
    //           console.log(response.employedata)
    //           this.setState({ userdata:response.employedata})
    //           // this.parseJSON(this.state)
    //         })
    //       }

    //     )
    //     }





      change  = e => {
        this.setState ({
          [e.target.name]: e.target.Value
        });
      };
      getWebsite = () =>
      {
          fetch('/');
      }
      onSubmit = (event) => 
      {

          event.preventDefault();
          console.log(this.state);
          this.setState ({
           id: this.props.match.params.id,

          })
          console.log(this.state)
          axios.post(`${API_URL}employe/update`,this.state,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
        .then(function(response){
             if(response.data.success){
            //   console.log(json);
            alert(response.data.msg);
            this.props.history.push('/ViewEmployee')
        }
        else{
          console.log(response);
          alert(response.data.msg)
      }
    }.bind(this))
    .catch(error => {
        console.log(error.response);
      })
      };

    //   fileSelectedHandler = e => {     
    //       e.preventDefault();
    //     let files = e.target.files;
    //     console.log('data',files[0]);
    //   }
  render() {
    // const editmeet = this.props.editmeet;
    return (
        <div className="skin-blue fixed-layout">
        <div className="page-wrapper">

             <div className="container-fluid">

               <div className="row page-titles">
                   <div className="col-md-5 align-self-center">
                       {/* <h4 className="text-themecolor">Forms</h4> */}
                       <nav aria-label="breadcrumb">
                            <ol class="breadcrumb">
                                <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                                <li className="breadcrumb-item active" aria-current="page">Edit Employe</li>
                            </ol>
                            </nav>
                   </div>

               </div>


            </div>

    {/*Form content begin */}

<div className = "product-form-upper">
    <div className = "container">
    <div className = "below-custom-form">
        <div className = "row">
           <div className = "col-lg-12 col-md-12 col-sm-12 col-xs-12">
                   <h3>Edit Employe</h3>
           </div>
        </div>


                    <form className="custom-content-form" autoComplete = 'no'>

       <div class="form-row">
         <div class="form-group col-md-3">
         <label for="name">Enter Name</label>
          <input type="text" className="form-control" placeholder="blank" name="employe_name" value = {this.state.employe_name} onChange={(e) => this.setState({employe_name: e.target.value})} />
         </div>
         <div class="form-group col-md-3">
         <label for="email">Enter Email</label>
             <input type="email" className="form-control" placeholder="email" name="email" value = {this.state.email} onChange={(e) => this.setState({email: e.target.value})}/>
         </div>
         <div class="form-group col-md-3">
         <label for="adminname">Enter Spoc</label><br />              
         <select className="form-control" name="position" value={this.state.position} onChange={(e) => this.setState({position: e.target.value})}>
        <option selected>Choose......</option>
        <option value="0">Service Exec</option>
        <option value="1">Supervisor</option>
        </select>
         </div>
         <div class="form-group col-md-3">
         <label for="number">Enter Blood Group</label>
             <input type="text" className="form-control" placeholder=" contact number" name="blood_group" value = {this.state.blood_group} onChange={(e) => this.setState({blood_group: e.target.value})}/>
         </div>

         <div class="form-group col-md-4">
         <label for="number">Enter Contact Number</label>
             <input type="number" className="form-control" placeholder=" contact number" name="mobile1" value = {this.state.mobile1} onChange={(e) => this.setState({mobile1: e.target.value})}/>
         </div>
         <div class="form-group col-md-4">
         <label for="number">Enter Alternate Contact Number</label>
             <input type="number" className="form-control" placeholder=" contact number" name="mobile2" value = {this.state.mobile2} onChange={(e) => this.setState({mobile2: e.target.value})}/>
         </div>
         <div class="form-group col-md-4">
         <label for="number">Enter Emergency Contact Number</label>
             <input type="number" className="form-control" placeholder=" contact number" name="emergency_contact_number" value = {this.state.emergency_contact_number} onChange={(e) => this.setState({emergency_contact_number: e.target.value})}/>
         </div>
         <div class="form-group col-md-12">
         <label for="number">Enter Account Detail</label>
             <input type="text" className="form-control" placeholder=" contact number" name="account_detail" value = {this.state.account_detail} onChange={(e) => this.setState({account_detail: e.target.value})}/>
         </div>
       </div>

       <button class="btn btn-primary" onClick={this.onSubmit.bind(this)}>Submit</button>

     </form>


    </div>
    </div>
</div>






        </div>
        </div>

    )
  }
}

export default withRouter(EditEmploye)
