      s_t4: map.s_t4,
      s_t5: map.s_t5,
      s_t6: map.s_t6,
      s_t7: map.s_t7,
      s_t8: map.s_t8,
      s_t9: map.s_t9,
      s_t10: map.s_t10,
      s_t11: map.s_t11,
        });
        console.log(this.state);
      };
      componentWillMount = () => {
        var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth() + 1; //January is 0!
        var yyyy = today.getFullYear();

        if (dd < 10) {
          dd = "0" + dd;
        }

        if (mm < 10) {
          mm = "0" + mm;
        }

        today = mm + "/" + dd + "/" + yyyy;
        console.log("date here", today);
        this.state.date = today;
        console.log(this.state.date);
      };

      change = e => {
        this.setState({
          [e.target.name]: e.target.value
        });
      };
      showformhandle() {
