import React, { Component } from 'react'
import '../Test/Test.css'
import { Link, NavLink, Redirect } from 'react-router-dom'
import axios from 'axios'
import { API_URL } from '../../services/url'
import ReactTable from 'react-table'

export class CustomerView extends Component {
  constructor() {
    super()
    this.state = {
      customersList: []
    }
  }

  async componentDidMount() {
    const tokenvalue = localStorage.getItem('token')
    console.log('value', tokenvalue)
    try {
      const response = await axios.get(
        `${API_URL}master/customer`,
        (axios.defaults.headers.common['x-access-token'] = tokenvalue)
      )
      console.log(response)
      if (response.status === 200) {
        this.setState({ customersList: response.data.CUSTOMER_NAME })
      }
    } catch (error) {
      console.log(error)
    }
  }

  render() {
    const columns = [
      {
        Header: 'Customer Code',
        accessor: 'CUSTOMER_CODE',
        sortable: false
      },
      {
        Header: 'Customer Name',
        accessor: 'CUSTOMER_NAME',
        sortable: true
      },
      {
        Header: 'Bill To Number',
        accessor: 'BILL_TO_NUMBER',
        sortable: false
      },
      {
        Header: 'Bill to Address',
        accessor: 'BILL_TO_ADDRESS',
        sortable: false
      },
      {
        Header: 'Ship To Number',
        accessor: 'SHIP_TO_NUMBER',
        sortable: false
      },
      {
        Header: 'Ship To Address',
        accessor: 'SHIP_TO_ADDRESS',
        sortable: false
      },
      {
        Header: 'Created On',
        accessor: 'CREATION_DATE',
        sortable: false
      },
      {
        Header: 'Status',
        accessor: 'STATUS',
        sortable: false
      }
    ]

    return (
      <div className='skin-blue fixed-layout'>
        <div className='page-wrapper'>
          <div className='container-fluid'>
            <div className='row page-titles'>
              <div className='col-md-5 align-self-center'>
                <nav aria-label='breadcrumb'>
                  <ol class='breadcrumb'>
                    <li className='breadcrumb-item'>
                      <Link to={process.env.PUBLIC_URL + '/'}>Home</Link>
                    </li>
                    <li className='breadcrumb-item active' aria-current='page'>
                      Plant Customer's
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>
          <div className='container my-3 w-100'>
            <NavLink to='LocalCustomerView' className='mb-3' style={{ float: 'right', marginRight: '1.75rem' }}>
              <button className='btn btn-info'>View Local Customer's</button>
            </NavLink>
          </div>
          <div className='container'>
            <ReactTable
              columns={columns}
              data={this.state.customersList}
              className='table table-light -striped -highlight table-responsive text-center'
              headerClassName='bg-info'
              showPagination
              defaultPageSize={20}
              id='customersTable'
              noDataText='Data not found.'
              ref={(r) => {
                this.selectTable = r
              }}
            />
          </div>
        </div>
      </div>
    )
  }
}

export default CustomerView
