import React, { Component } from 'react'
import '../Test/Test.css'
import { Link, NavLink } from 'react-router-dom'
import axios from 'axios'
import { API_URL } from '../../services/url'
import ReactTable from 'react-table'

export class LocalVendorView extends Component {
  constructor(props) {
    super(props)

    this.state = {
      localVendors: []
    }
  }

  componentDidMount() {
    this.fetchData()
  }

  async fetchData() {
    const tokenvalue = localStorage.getItem('token')
    console.log('value', tokenvalue)
    try {
      const response = await axios.get(
        `${API_URL}master/viewLocalvendor`,
        (axios.defaults.headers.common['x-access-token'] = tokenvalue)
      )
      console.log(response)
      if (response.status === 200) {
        this.setState({ localVendors: response.data.Vendor })
      }
    } catch (error) {
      console.log(error)
    }
  }

  async addLocalVendor() {
    const tokenvalue = localStorage.getItem('token')
    let data = {
      "Site": document.getElementById('vendorAddress').value,
      "status": document.getElementById('vendorStatus').value,
      "Name": document.getElementById('vendorName').value,
      "item": document.getElementById('vendorItem').value,
      "code": document.getElementById('orgCode').value
    }
    console.log('value', tokenvalue)
    try {
      const response = await axios.post(
        `${API_URL}master/addLocalvendor`, data,
        (axios.defaults.headers.common['x-access-token'] = tokenvalue)
      )
      console.log(response)
      if (response.status === 200) {
        window.alert('Local vendor added successfully')
      }
    } catch (error) {
      window.alert('Something went wrong')
      console.log(error)
    }
    this.fetchData()
  }

  render() {
    const columns = [
      {
        Header: 'Trial Number',
        accessor: 'Trial_number',
        sortable: false
      },
      {
        Header: 'Vendor Name',
        accessor: 'Vendor_Name',
        sortable: true
      },
      {
        Header: 'Vendor Site',
        accessor: 'Vendor_site',
        sortable: false
      },
      {
        Header: 'Created On',
        accessor: 'creation_date',
        sortable: false
      },
      {
        Header: 'Item Description',
        accessor: 'ITEM_DESCRIPTION',
        sortable: false
      },
      {
        Header: 'Organization Code',
        accessor: 'Organization_code',
        sortable: false
      },
      {
        Header: 'Status',
        accessor: 'Vendor_State',
        sortable: false
      }
    ]

    return (
      <div className='skin-blue fixed-layout'>
        <div className='page-wrapper'>
          <div className='container-fluid'>
            <div className='row page-titles'>
              <div className='col-md-5 align-self-center'>
                <nav aria-label='breadcrumb'>
                  <ol class='breadcrumb'>
                    <li className='breadcrumb-item'>
                      <Link to={process.env.PUBLIC_URL + '/'}>Home</Link>
                    </li>
                    <li className='breadcrumb-item active' aria-current='page'>
                      Local Vendor's
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>
          <div className='container my-3 w-100'>
            <NavLink to='VendorView' className='mx-4'>
              <span className='text-info text-sm-center font-weight-normal'><i class='fa fa-arrow-circle-left mr-1' aria-hidden='true' />Go Back</span>
            </NavLink>
            <button className='btn mr-4 mb-2 btn-success' style={{ float: 'right', lineHeight: '1.2' }} data-toggle='modal' data-target='#addVendorModal'>Add Local Vendor</button>
          </div>
          <div className='container'>
            <ReactTable
              columns={columns}
              data={this.state.localVendors}
              className='table table-light -striped -highlight table-responsive text-center'
              headerClassName='bg-info'
              showPagination
              defaultPageSize={10}
              id='customersTable'
              noDataText='Data not found.'
              ref={(r) => {
                this.selectTable = r
              }}
            />
          </div>

          <div class='modal fade' id='addVendorModal' tabIndex='-1' role='dialog' aria-labelledby='exampleModalCenterTitle' aria-hidden='true'>
            <div class='modal-dialog modal-dialog-centered' role='document'>
              <div class='modal-content'>
                <div class='modal-header'>
                  <h5 class='modal-title' id='exampleModalCenterTitle'>Add Local Vendor</h5>
                  <button type='button' class='close' data-dismiss='modal' aria-label='Close'>
                    <span aria-hidden='true'>&times;</span>
                  </button>
                </div>
                <div class='modal-body'>
                  <form>
                    <div class='form-group'>
                      <label htmlFor='vendorName' class='col-form-label mr-2'>Vendor Name:</label>
                      <input type='text' class='form-control' id='vendorName' minLength='5' onFocus />
                    </div>
                    <div class='form-group'>
                      <label htmlFor='vendorAddress' class='col-form-label mr-2'>Site Address:</label>
                      <input type='text' class='form-control' id='vendorAddress' minLength='10' />
                    </div>
                    <div class='form-group'>
                      <label htmlFor='vendorStatus' class='col-form-label mr-2'>Vendor Status:</label>
                      <input type='text' class='form-control small-input' id='vendorStatus' minLength='1' />
                      <label htmlFor='orgCode' class='col-form-label ml-3 mr-2'>Organization Code:</label>
                      <input type='text' class='form-control small-input' id='orgCode' minLength='1' />
                    </div>
                    <div class='form-group'>
                      <label htmlFor='vendorItem' class='col-form-label mr-2'>Item Description:</label>
                      <input type='text' class='form-control' id='vendorItem' minLength='1' />
                    </div>
                  </form>
                </div>
                <div class='modal-footer'>
                  <button type='button' class='btn btn-secondary' data-dismiss='modal'>Close</button>
                  <button type="button" class="btn btn-primary" data-dismiss="modal" onClick={this.addLocalVendor.bind(this)}>Add</button>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>
    )
  }
}

export default LocalVendorView
