                    </label>
                    <div class="col-sm-8">
                      {/* <p>para here </p> */}
                      {this.state.material_passing}
                      {/* {this.state.total || 'NO DATA'} */}
                    </div>
                  </div>
                  {/* <button
                    className="btn btn-danger"
