import React, { Component } from "react";
import { Link, withRouter, Redirect } from "react-router-dom";
import "./Employee.css";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
class Employee extends Component {
  constructor(props) {
    super(props);
    this.state = {
      employee_id: "",
      name: "",
      role_id: "",
      email: "",
      number: "",
      password: "ABC@123"
      // designation: "",
      // login_email : ""
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.validator = new SimpleReactValidator();
  }

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getWebsite = () => {
    fetch("/").then(console.log(this.state));
  };
  async onSubmit(event) {
    event.preventDefault();
    let tokenvalue = localStorage.getItem("token");
    console.log("value", tokenvalue);
    if (this.validator.allValid()) {
      try {
        const response = await axios.post(
          `${API_URL}employee/add`,
          this.state,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        );
        console.log(response);

        //   .then(console.log(this.state));

          if (response.data.success) {
            alert(response.data.msg);
            this.props.history.push("/ViewEmployee");
          } else {
            alert(response.data.msg);
          }
      } catch (error) {
        console.log(error.response.data.error);
        alert(error.response.data.error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
  }
  render() {
    return (
      <div className="skin-blue fixed-layout">
        <div className="page-wrapper">
          <div className="container-fluid">
            <div className="row page-titles">
              <div className="col-md-5 align-self-center">
                {/* <h4 className="text-themecolor">Forms</h4> */}
                <nav aria-label="breadcrumb">
                  <ol class="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to={process.env.PUBLIC_URL + "/"}>Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                      Add Employee
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>

          {/*Form content begin */}

          <div className="product-form-upper">
            <div className="container">
              <div className="below-custom-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Add Employee</h3>
                    <hr />
                    <br />
                  </div>
                </div>
                <form className="custom-content-form">
                  <div className="form-row">
                    <div class="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputPassword"
                          class="col-sm-2 col-form-label"
                        >
                          Employe Id
                        </label>
                        <div class="col-sm-10">
                          <input
                            type="text"
                            class="form-control"
                            id="inputPassword"
                            name="employee_id"
                            value={this.state.employee_id}
                            onChange={e => this.change(e)}
                          />
                          <span className="text-danger">
                            {this.validator.message(
                              "id",
                              this.state.employee_id,
                              "required"
                            )}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div class="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputPassword"
                          class="col-sm-2 col-form-label"
                        >
                          Employe Name
                        </label>
                        <div class="col-sm-10">
                          <input
                            type="text"
                            class="form-control"
                            id="inputPassword"
                            name="name"
                            value={this.state.name}
                            onChange={e => this.change(e)}
                          />
                          <span className="text-danger">
                            {this.validator.message(
                              "name",
                              this.state.name,
                              "required|alpha_space"
                            )}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div class="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputSubcategory"
                          className="col-sm-2 col-form-label"
                        >
                          Role
                        </label>
                        <div class="col-sm-10">
                          <select
                            className="form-control"
                            value={this.state.role_id}
                            name="role_id"
                            onChange={e => this.change(e)}
                          >
                            <option value="0">Choose...</option>
                            <option value="1">Admin</option>
                            <option value="2">Sr. Manager</option>
                            <option value="3">Business Manager</option>
                            <option value="4">Quality Incharge</option>
                            <option value="5">Plant Technical Officer</option>
                            {/* <option value="6">Field Technical Incharge</option> */}
                          </select>
                          <span className="text-danger">
                            {this.validator.message(
                              "role",
                              this.state.role_id,
                              "required"
                            )}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div class="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputPassword"
                          class="col-sm-2 col-form-label"
                        >
                          Contact Number
                        </label>
                        <div class="col-sm-10">
                          <input
                            type="number"
                            class="form-control"
                            id="inputPassword"
                            name="number"
                            value={this.state.number}
                            onChange={e => this.change(e)}
                          />
                          <span className="text-danger">
                            {this.validator.message(
                              "number",
                              this.state.number,
                              "required|numeric|min:10|max:10"
                            )}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </form>
              </div>
              {/*Login Form */}
              <div className="below-custom-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Login Detail</h3>
                    <hr />
                    <br />
                  </div>
                </div>
                <form className="custom-content-form">
                  <div class="form-group row">
                    <label
                      for="inputSubcategory"
                      className="col-sm-2 col-form-label"
                    >
                      Email ID
                    </label>
                    <div class="col-sm-10">
                      <input
                        type="email"
                        className="form-control"
                        value={this.state.email}
                        name="email"
                        onChange={e => this.change(e)}
                      />
                      <span className="text-danger">
                        {this.validator.message(
                          "email",
                          this.state.email,
                          "required|email"
                        )}
                      </span>
                    </div>
                  </div>
                  <div class="form-group row">
                    <label
                      for="inputSubcategory"
                      className="col-sm-2 col-form-label"
                    >
                      Password
                    </label>
                    <div class="col-sm-10">
                      <input
                        type="password"
                        className="form-control"
                        value={this.state.password}
                        name="password"
                       disabled
                      />
                      <span className="text-danger">
                        {this.validator.message(
                          "password",
                          this.state.password,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>
                  <small>* This Fields are Mandatory . </small>
                  <br />
                  <button
                    class="btn btn-primary"
                    onClick={this.onSubmit.bind(this)}
                  >
                    Submit
                  </button>
                </form>
              </div>
              {/*end login form */}
            </div>
          </div>
        </div>
      </div>
    );
  }
