import React, { Component } from "react";
import "./Employee.css";
import { Link } from "react-router-dom";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
export class UserList extends Component {
  constructor() {
    super();
    this.state = {
      userdata: [],
      detailsdata: [],
      currentPage: 1,
      todosPerPage: 10,
      designation: ""
    };
    this.handlepagenumber = this.handlepagenumber.bind(this);
    this.handleCheck = this.handleCheck.bind(this);
    this.detailCheck = this.detailCheck.bind(this);
  }
  handlepagenumber(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }
  handleCheck(todo) {
    console.log(todo.employe_id);
    let sitemeet = todo.employe_id;
    axios
      .get(
        `${API_URL}employe/delete?id=${sitemeet}`,
        (axios.defaults.headers.common["authorization"] =
          "Bearer " + authService.getToken())
      )
      .then(
        function(json) {
          if (json.data.success === true) {
            //   console.log(json);
            //  window.location.reload('/ViewAdmin')
            alert("Employee has been deleted ");
            // window.location.reload();
            this.handleClick();
          } else {
            console.log(json);
          }
        }.bind(this)
      );
  }
  detailCheck(todo) {
    // console.log(item.admin_id);
    let sitemeet = todo.employe_id;
    axios
      .get(
        `${API_URL}employe/detail?id=${sitemeet}`,
        (axios.defaults.headers.common["authorization"] =
          "Bearer " + authService.getToken())
      )
      .then(response => {
        //console.log(response.employedata)
        this.setState({ detailsdata: response.data.employedata });
        // this.parseJSON(this.state)
      });
  }

  componentDidMount() {
    this.handleClick();
  }
  async handleClick() {
    try {
      const response = await axios.get(
        `${API_URL}employee/view`,
        (axios.defaults.headers.common["authorization"] =
          "Bearer " + authService.getToken())
      );
      console.log(response);

      // if (response.data.success) {
      //   this.setState({ userdata: response.data.user_data });
      // }
      // this.parseJSON(this.state)
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    const { todos, currentPage, todosPerPage } = this.state;

    let renderTodos;

    // Logic for displaying todos
    const indexOfLastTodo = currentPage * todosPerPage;
    const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
    const currentTodos = this.state.userdata.slice(
      indexOfFirstTodo,
      indexOfLastTodo
    );

    let serial = 0;
    // let employedatatable;
    if (this.state.userdata.length === 0) {
      renderTodos = (
        <div>
          <br />
          <center>
            <h4>
              <strong>There Is No Employe Addedd Yet.</strong>
            </h4>
          </center>
        </div>
      );
    } else {
      renderTodos = currentTodos.map((todo, index) => {
        return (
          <tr key={index}>
            {/* <th scope="row">{++serial}</th> */}
            <td>{todo.name || "NO DATA"}</td>
            <td>{todo.role_id}</td>
            <td>{todo.email_id || "NO DATA"}</td>
            <td>{todo.contact_number || "NO DATA"}</td>
            <td>
                      <Link to ={process.env.PUBLIC_URL +`/EditEmploye/${todo.employe_id}`}>
      <span class="tooltip-toggle" aria-label="Edit" tabindex="0">
        <button className="btn btn-warning custom-edit-btn btn-sm" disabled><i class="fa fa-pencil" aria-hidden="true"></i></button>
        </span>
        </Link>
        <span class="tooltip-toggle" aria-label="Suspend" tabindex="0">
           <button  className="btn btn-danger custom-edit-btn btn-sm" onClick={this.handleCheck.bind(this, todo)} disabled><i class="fa fa-trash-o" aria-hidden="true"></i></button>
          </span>

