import React, { Component } from "react";
import { Link, Redirect, withRouter } from "react-router-dom";
import "./Zone.css";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
class Zone extends Component {
  constructor(props) {
    super(props);
    this.state = {
      region: ""
      //    pin : ""
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.validator = new SimpleReactValidator();
  }

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getWebsite = () => {
    fetch("/").then(console.log(this.state));
  };
  async onSubmit(event) {
    event.preventDefault();
    let tokenvalue = localStorage.getItem("token");
    if (this.validator.allValid()) {
      let body = {
        region: this.state.region
      };
      console.log('value',body)
      try {
        const response = await axios.post(
          `${API_URL}region/add`,
          body,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        );
        console.log(response);
        if (response.data.success) {
          alert(response.data.msg);
          this.props.history.push("/ZoneLoc");
        } else {
          alert(response.data.msg);
        }
      } catch (error) {
        console.log(error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
    // console.log(this.state);
  }
  render() {
    // const { region, ...props } = this.props;

    return (
      <div className="skin-blue fixed-layout">
        <div className="page-wrapper">
          <div className="container-fluid">
            <div className="row page-titles">
              <div className="col-md-5 align-self-center">
                {/* <h4 className="text-themecolor">Forms</h4> */}
                <nav aria-label="breadcrumb">
                  <ol class="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to={process.env.PUBLIC_URL + "/"}>Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                      Add Region
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>

          {/*Form content begin */}

          <div className="product-form-upper">
            <div className="container">
              <div className="below-custom-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Add Region</h3>
                    <hr />
                    <br />
                  </div>
                </div>
                <form className="custom-content-form" method="POST">
                  <div className="form-group row">
                    <label for="inputPassword" class="col-sm-2 col-form-label">
                      Region Name
                    </label>
                    <div class="col-sm-10">
                      <input
                        type="text"
                        class="form-control"
                        id="inputPassword"
                        placeholder="Enter Region Name"
                        name="region"
                        value={this.state.region}
                        onChange={e => this.change(e)}
                      />
                      <span className="text-danger">
                        {this.validator.message(
                          "region",
                          this.state.region,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>

                  <button class="btn btn-primary" onClick={this.onSubmit}>
                    Submit
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
// Zone.propTypes = {
//   region: PropTypes.string
// };
export default withRouter(Zone);
