import React, { Component } from "react";
import { Link, Redirect, withRouter, NavLink } from "react-router-dom";
import "./Test.css";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
import { SuccessModal } from "./SuccessModal";
export class Asset extends Component {
  constructor(props) {
    super(props);
    this.state = {
      btnstatus  :false,
      pid: "",
      asset_type_id: "",
      description: "",
      yom: "",
      asset_img: "",
      asset_make: "",
      number: "",
      userdata:[]
    };
    this.validator = new SimpleReactValidator();
  }
  componentDidMount() {
    this.asset();

  }
  async asset() {
    let tokenvalue = localStorage.getItem("token");
    console.log('value', tokenvalue)
    try {
      const response = await axios.get(
        `${API_URL}asset_type/view`,
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
      );

      console.log(response.data);
      if (response.data.success) {
       await this.setState({
          userdata: response.data.assetTypedata,
        });

      }
    } catch (error) {
      console.log(error.response);
    }
  }
  openModal() {
    this.setState({
        visible : true
    });
}

  closeModal() {
    this.setState({
        visible : false
    });
  }
  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getWebsite = () => {
    fetch("/");
  };
  async onSubmit(e) {
    e.preventDefault();
    console.log(this.state);
    if (this.validator.allValid()) {
        let tokenvalue = localStorage.getItem("token");
      let body = {
        //pid: localStorage.getItem("plant_name"),
        asset_type_id: this.state.asset_type_id,
        description: this.state.description,
        yom: this.state.yom,
        asset_img: this.state.asset_img,
        make: this.state.asset_make,
        number:this.state.number
      };
    //   this.props.history.push("/ViewAssetPlant");
      console.log("body state hre", body);
      this.openModal();
      this.setState({
        btnstatus : true
      })
      try {
        const response = await axios.post(
            `${API_URL}asset/add`,
           body,
            (axios.defaults.headers.common["x-access-token"] = tokenvalue)
          );

        console.log(response);
        // this.props.history.push("/ViewArea");
        //   .then(console.log(this.state));
        // .then(
        //   function(response) {
        //     //console.log(error);
            if (response.data.success) {
              alert(response.data.msg);
              this.props.history.push("/ViewAssets");
            } else {
              alert(response.data.msg);
            }
        //   }.bind(this)
        // )
        // .catch(error => {
        //   // alert(error.response.data.msg)
        //   console.log(error.response);
        // });
      } catch (error) {
        console.log(error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
  }
  render() {
         // let showmodal;
         let sbmtbtn, btnmsg;
         if(this.state.visible)
         {
           // showmodal = (
           //   <div>
           //     <SuccessModal  
           //          visible={this.state.visible}
