import React, { Component } from 'react';
import './Zone.css';
import {Link, withRouter} from 'react-router-dom';
import axios from 'axios';
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
class EditZone extends Component {
  constructor(props)
  {
      super(props)
      this.state = {
        id : this.props.match.params.id,
        zone_name: "",
         };

        this.change = this.change.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillMount ()
  {
    this.handleClick();
  }
  handleClick () {
    let Editid = this.props.match.params.id;
    //console.log(this.props.match.params.id)
      axios.get(`${API_URL}zone/detail?id=${Editid}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
          //console.log(response.zonedata)
          //this.setState({ data:response.zonedata})
          this.setState({zone_name:response.data.zonedata[0].zone_name})


        })

    }



    change  = e => {
      this.setState ({
        [e.target.name]: e.target.value
      });
    };

    onSubmit = (event) =>
    {
        event.preventDefault();
        console.log(this.state);
        this.setState ({
          id : this.props.match.params.id
        })
        axios.post(`${API_URL}zone/update`,this.state,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then(function(response){
           if(response.data.success){
          //   console.log(json);
          alert(response.data.msg);
          this.props.history.push('/ZoneLoc')
      }
      else{
        console.log(response);
        alert(response.data.msg)
    }
  }.bind(this))
    };
render() {

  return (
      <div className="skin-blue fixed-layout">
      <div className="page-wrapper">

           <div className="container-fluid">

             <div className="row page-titles">
                 <div className="col-md-5 align-self-center">
                     {/* <h4 className="text-themecolor">Forms</h4> */}
                     <nav aria-label="breadcrumb">
                          <ol class="breadcrumb">
                              <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                              <li className="breadcrumb-item active" aria-current="page">Edit Zone</li>
                          </ol>
                          </nav>
                 </div>

             </div>


          </div>

  {/*Form content begin */}

<div className = "product-form-upper">
  <div className = "container">
  <div className = "below-custom-form">
      <div className = "row">
         <div className = "col-lg-12 col-md-12 col-sm-12 col-xs-12">
                 <h3>Edit Zone</h3>
         </div>
      </div>

      <form className="custom-content-form" autoComplete = 'no'>
<div class="form-row">
  <div class="form-group col-md-12">
  <label for="inputCategory">Enter Name</label>
   <input type="text" className="form-control" placeholder="John Doe" name="zone_name" value = {this.state.zone_name} onChange={e => this.change(e)} required/>
  </div>
</div>

<button class="btn btn-primary" onClick = {this.onSubmit.bind(this)}>Submit</button>
</form>
  </div>
  </div>
</div>
      </div>
      </div>

  )
}
}
export default withRouter(EditZone)
