import React, { Component } from "react";
import { Link, withRouter, Redirect } from "react-router-dom";
import "./Area.css";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
export class AreaAllocate extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emp_id: "",
      region_id: "",
      from_date: "",
      to_date: "",
      area_id: "",
      userdata: [],
      employee: [],
      area:[]
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.validator = new SimpleReactValidator();
  }

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  componentDidMount() {
    this.region();

  }

  async region() {
    let tokenvalue = localStorage.getItem("token");
    console.log('value', tokenvalue)
    try {
      const response = await Promise.all( [axios.get(
        `${API_URL}region/view`,
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
      ),axios.get(
        `${API_URL}employee/view_bm`,
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        ),
        axios.get(
          `${API_URL}area/view`,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        )]);

      console.log(response[2].data);
      if (response.length>0) {
       await this.setState({
          userdata: response[0].data.Regiondata,
          employee: response[1].data.User,
          area:response[2].data.Area
        });

      }
    } catch (error) {
      console.log(error);
    }
  }
  async onSubmit(event) {
    event.preventDefault();
    let tokenvalue = localStorage.getItem("token");
    if (this.validator.allValid()) {
    console.log(this.state);
    try {
      const response = await axios.post(
        `${API_URL}area/user_area`,
        {
          user_id: this.state.emp_id,
          area_id: this.state.area_id,
          start: this.state.from_date,
          // stop: this.state.to_date,
          status:2
        },
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
      );
     console.log(response);
        if (response.data.success) {
          alert(response.data.msg);
          this.props.history.push("/AllocatedArea");
        } else {
          alert(response.data.msg);
        }
    } catch (error) {
      console.log(error);
    }
  } else {
    this.validator.showMessages();
    // rerender to show messages for the first time
    this.forceUpdate();
  }
  }
  render() {
    return (
      <div className="skin-blue fixed-layout">
        <div className="page-wrapper">
          <div className="container-fluid">
            <div className="row page-titles">
              <div className="col-md-5 align-self-center">
                {/* <h4 className="text-themecolor">Forms</h4> */}
                <nav aria-label="breadcrumb">
                  <ol class="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to={process.env.PUBLIC_URL + "/"}>Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                      Area Allocation
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>

          {/*Form content begin */}

          <div className="product-form-upper">
            <div className="container">
              <div className="below-custom-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Area Allocation</h3>
                    <br />
                  </div>
                </div>
                <form className="custom-content-form" method="POST">
                  <div className="form-group row">
                    <label for="inputPassword" class="col-sm-1 col-form-label">
                      Employee Name
                    </label>
                    <div class="col-sm-11">
                    <select className="form-control" name="emp_id" value={this.state.emp_id}  onChange={(e) => this.setState({emp_id: e.target.value})}>
                            <option>All Employee</option>
                    {
                              this.state.employee ?
                              this.state.employee.map(function(item, id) {
                                return(

                        <option key ={id} value={item.uid}>{item.name}</option>
                      )
                              }
                      )
                              :
                              <span>Data is loading....</span>
                            }

                    </select>

                      <span className="text-danger">
                        {this.validator.message(
                          "name",
                          this.state.emp_id,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>
                  <div class="form-group row">
                    <label
                      for="inputSubcategory"
                      className="col-sm-1 col-form-label"
                    >
                      Region Name
                    </label>
                    <div class="col-sm-11">
                    <select className="form-control" name="region_id" value={this.state.region_id}  onChange={(e) => this.setState({region_id: e.target.value})}>
                                <option>All Region</option>
                        {
                                  this.state.userdata ?
                                  this.state.userdata.map(function(item, id) {
                                    return(

                            <option key ={id} value={item.region_id}>{item.region_name}</option>
                          )
                                  }
                          )
                                  :
                                  <span>Data is loading....</span>
                                }

                        </select>
                      <span className="text-danger">
                        {this.validator.message(
                          "region",
                          this.state.region_id,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>
                  <div class="form-group row">
                    <label
                      for="inputSubcategory"
                      className="col-sm-1 col-form-label"
                    >
                      Area Name
                    </label>
                    <div class="col-sm-11">
                    <select className="form-control" name="area_id" value={this.state.area_id}  onChange={(e) => this.setState({area_id: e.target.value})}>
                                <option>All Area</option>
                        {
                                  this.state.area ?
                                  this.state.area.filter(item => item.region_id == this.state.region_id).map(function(item, id) {
                                    return(

                            <option key ={id} value={item.area_id}>{item.area_name}</option>
                          )
                                  }
                          )
                                  :
                                  <span>Data is loading....</span>
                                }

                        </select>
                      <span className="text-danger">
                        {this.validator.message(
                          "area",
                          this.state.area_id,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputSubcategory"
                          className="col-sm-2 col-form-label"
                        >
                          From Date
                        </label>
                        <div class="col-sm-10">
                          <input
                            type="date"
                            className="form-control"
