import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import './Locations.css';
import axios from 'axios'
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';

export class ViewLocations extends Component {
    constructor () {
        super() 
        this.state = {
          userdata : [],
          detailsdata : [],
          currentPage: 1,
          todosPerPage: 10
        }
        this.handlepagenumber = this.handlepagenumber.bind(this);
        this.handleCheck = this.handleCheck.bind(this);
        this.detailCheck = this.detailCheck.bind(this);
    }
    handlepagenumber(event) {
      this.setState({
        currentPage: Number(event.target.id)
      });
    }
    handleCheck(todo) {
      console.log(todo.location_id);
      let sitemeet = todo.location_id;
      if(window.confirm('Are You Sure You Want To Delete This Location?'))
      {
      axios.get(`${API_URL}location/delete?id=${sitemeet}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
                   .then(function(response){
                        if(response.data.success){
                      //  alert(response.data.msg);
                        // window.location.reload()
                        this.handleClick()

                   }
                   else{
                     console.log(response);
                 }
                 }.bind(this))
                 .catch(error => {
                  console.log(error.response);
                })
              }
     }
     detailCheck(todo) {
      // console.log(item.admin_id);
      let sitemeet = todo.location_id;
      fetch(`${API_URL}location/detail?id=${sitemeet}`,{
                 method : 'GET',
                 headers : {
                   'Authorization': 'Bearer ' + this.state.token,
                   "Content-Type" : "application/json"
               }
               })
               .then((res) => {
                res.json().then((response) => { 
                  console.log(response.locationdata)
                  this.setState({ detailsdata:response.locationdata})
                  // this.parseJSON(this.state)
                })
              }

            )
     }


    componentWillMount ()
    {
      this.handleClick();
    }
    handleClick () {
      axios.get(`${API_URL}location/view`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.data.locationdata)
            if(response.data.success)
            {
            this.setState({ userdata:response.data.locationdata})
            }
            // this.parseJSON(this.state)
          })
          .catch(error => {
            console.log(error.response);
          })

      }
        render() {
          const { todos, currentPage, todosPerPage } = this.state;

          let renderTodos

          // Logic for displaying todos
          const indexOfLastTodo = currentPage * todosPerPage;
          const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
          const currentTodos = this.state.userdata.slice(indexOfFirstTodo, indexOfLastTodo);


          let locationdatatable;
          let serial = 0
          if(this.state.userdata.length === 0) 
          {
            renderTodos = (
              <div>
                <br />
                <center><h4><strong>There is NO Location Addedd Yet.</strong></h4></center>
              </div>
            )
          }
          else{
            renderTodos = currentTodos.map((todo, index) => {
              return (

                   <tr key = {index}>
                   {/* <th scope="row">{++serial}</th> */}
                      <td>{todo.zone_name || "NO DATA"}</td>
                      <td>{todo.location_name || 'NO DATA'}</td>
                      <td>{todo.location_pin || 'NO DATA'}</td>
                      <td>
                      <Link to ={process.env.PUBLIC_URL +`/EditLocation/${todo.location_id}`}> 
               <span class="tooltip-toggle" aria-label="Edit" tabindex="0">
               <button className="btn btn-warning custom-edit-btn btn-sm"><i class="fa fa-pencil" aria-hidden="true"></i></button>
                     </span>    
                 </Link>
                 <span class="tooltip-toggle" aria-label="Suspend" tabindex="0">
           <button  className="btn btn-danger custom-edit-btn btn-sm" onClick={this.handleCheck.bind(this, todo)}><i class="fa fa-trash-o" aria-hidden="true"></i></button>
