import React, { Component } from 'react';
import './Admin.css';
import {Link, withRouter} from 'react-router-dom';
import axios from 'axios';
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
class EditAdmin extends Component {
    constructor(props)
    {
        super(props)
        this.state = {
            id : this.props.match.params.id,
                name : "",
                adminname : "",
                email : "",
                mobile1 : "",
                mobile2 : ""


           };
           this.onSubmit = this.onSubmit.bind(this);
          this.change = this.change.bind(this);
    }
    componentDidMount ()
    {
      this.handleClick();
    }
    handleClick () {
      let Editid = this.props.match.params.id;
      console.log(this.props.match.params.id)
        // // let meetupId = items.admin_id;
        // console.log(this.props)
        //     // console.log(meetupId);
        //     let sitemeet = item.admin_id;
      axios.get(`${API_URL}admin/edit?id=${Editid}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
        .then((response) => { 
            //console.log(response.admindata)
            // this.setState({ data: response.admindata })
            // this.parseJSON(this.state)
            this.setState({name:response.data.admindata[0].admin_full_name})
            this.setState({adminname:response.data.admindata[0].adminname})
            this.setState({email:response.data.admindata[0].admin_email})
            this.setState({mobile1:response.data.admindata[0].admin_mobile1})
            this.setState({mobile2:response.data.admindata[0].admin_mobile2})
          })
          .catch(error => {
            console.log(error.response);
          })
      }



      change  = e => {
        this.setState ({
          [e.target.name]: e.target.value 
        });
      };
      getWebsite = () =>
      {
          fetch('/');
      }
      onSubmit = (event) =>
      {
          event.preventDefault();
          console.log(this.state);
          this.setState ({
            id : this.props.match.params.id,
          })
       axios.post(`${API_URL}admin/update`,this.state,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
        .then(function(response){
          console.log(response.data)
             if(response.data.success){
            //   console.log(json);
            alert(response.data.msg);
            this.props.history.push('/AdminEmp')
        }
        else{
          alert(response.data.msg);
          // alert('please check the code');
      }
    }.bind(this))
    .catch(error => {
      console.log(error.response);
    })
      };
  render() {
    // const editmeet = this.props.editmeet;
    return (
        <div className="skin-blue fixed-layout">
        <div className="page-wrapper">

             <div className="container-fluid">

               <div className="row page-titles">
                   <div className="col-md-5 align-self-center">
                       {/* <h4 className="text-themecolor">Forms</h4> */}
                       <nav aria-label="breadcrumb">
                            <ol class="breadcrumb">
                                <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                                <li className="breadcrumb-item active" aria-current="page">Add Admin</li>
                            </ol>
                            </nav>
                   </div>

               </div>


            </div>

    {/*Form content begin */}

<div className = "product-form-upper">
    <div className = "container">
    <div className = "below-custom-form">
        <div className = "row">
           <div className = "col-lg-12 col-md-12 col-sm-12 col-xs-12">
                   <h3>Edit Admin NEW</h3>
           </div>
        </div>


        <form className="custom-content-form" autoComplete = 'no'>
  <div class="form-row">
    <div class="form-group col-md-4">
    <label for="inputCategory">Edit Name</label>
     <input type="text" className="form-control" name="name" value = {this.state.name} onChange={e => this.change(e)} />
    </div>
    <div class="form-group col-md-4">
    <label for="inputSubcategory">Edit User Name</label><br />              
     <input type="text" className = "form-control" name="adminname" value={this.state.adminname} onChange={e => this.change(e)} />
    </div>
    <div class="form-group col-md-4">
    <label for="inputSubcategory">Edit Email</label>
        <input type="email" className="form-control" name="email" value={this.state.email} onChange={e => this.change(e)} />
    </div>
    <div class="form-group col-md-6">
    <label for="inputSubcategory">Edit Mobile Number</label>
        <input type="number" className="form-control" name="mobile1" value={this.state.mobile1} onChange={e => this.change(e)} />
    </div>
    <div class="form-group col-md-6">
    <label for="inputSubcategory">Edit Alternate Mobile Number</label>
        <input type="email" className="form-control" name="mobile2" value={this.state.mobile2} onChange={e => this.change(e)} />
    </div>
  </div>

  <button class="btn btn-primary" onClick = {this.onSubmit.bind(this)}>Submit</button>
</form>

{/* <table className="table table-hover table-bordered ">
      <thead className="bg-info custom-thead-color">
        <tr>
          <th scope="col">Full Name</th>
          <th scope="col">Admin Name</th>
          <th scope="col">Email</th>
        </tr>
      </thead>
      <tbody>
      {
              this.state.data ?
              this.state.data.map(function(item, id) {
                return(

        <tr key = {id}>
          <th scope="row">{item.admin_id} '------'  {item.full_name}</th>
          <td>{item.adminname}</td> 
          <td>{item.email}</td> 
        </tr>
      )
              }, this
      )
              :
              <span>Data is loading....</span>
            }
      </tbody>
    </table> */}


    </div>
    </div>
</div>






        </div>
        </div>

    )
  }
}

export default withRouter(EditAdmin)
