import React, { Component } from 'react'
import { Link, Redirect, withRouter, NavLink } from "react-router-dom";
import "../RF-02/Rf2.css";
import axios from "axios";
import authService from "../../../services/auth-service";
import axiosService from "../../../services/axios-service";
import { API_URL } from "../../../services/url";
import SimpleReactValidator from "simple-react-validator";
import { RfButtonLink } from "../RfButtonLink";
import { SuccessModal } from "../../Test/SuccessModal";
export class VerifyRF3 extends Component {
    constructor(props) {
        super(props);
        this.state = {
            verify_comment : "",
          btnstatus: false,
          userdata : [],
          supplier: "",
          source: "",
          plant: "",
          date: "",
          s_t1: "",
          s_t2: "",
          s_t3: "",
          s_t4: "",
          s_t5: "",
          s_t6: "",
          s_t7: "",
          s_t8: " ",
          s_t9: "",
          s_t10: "",
          s_t11: "",
          s_t12: "",
          s_t13: "",
          s_t14: "",
          s_t15: "",
          s_t16: "",
          s_t17: "",
          s_t18: "",
          s_t19: "",
          s_t20: "",
          s_t21: "",
          s_t22: "",
          s_t23: "",
          s_t24: "",
          s_t25: "",
          remarks: ""
        };
        this.onSubmit = this.onSubmit.bind(this);
        this.validator = new SimpleReactValidator();
      }
      componentDidMount() {
        this.getFormDetail();
      }
      getFormDetail = async () => {
        // try {
        console.log(this.props.match.params.id);
        const response = await axios.get(
          `${API_URL}rf_form/view/${this.props.match.params.id}`,
          (axios.defaults.headers.common["x-access-token"] = localStorage.getItem(
            "token"
          ))
        );
        console.log("verifyrf21 data", response);
        let res = response.data.data;
        const map = {};
        res.map(async (item, i) => {
          console.log(item.question + "  " + item.answer);
          map[item.question] = item.answer;
        });

        console.log(map);
        await this.setState({
            verify_comment : response.data.meta.verify_comment,
            type: "RF_3",
            btnstatus:false,
            amount_status : false,
           plant : map.plant,
          date : map.date
        });
        console.log(this.state);
        //   // this.parseJSON(this.state)
        // } catch (error) {
        //   console.log(error);
        // }
      };
      componentWillMount = () => {
        var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth() + 1; //January is 0!
        var yyyy = today.getFullYear();

        if (dd < 10) {
          dd = "0" + dd;
        }

        if (mm < 10) {
          mm = "0" + mm;
        }

        today = mm + "/" + dd + "/" + yyyy;
        console.log("date here", today);
        this.state.date = today;
        console.log(this.state.date);
      };

      async componentDidMount() {
        await this.handleClick();
      }
      async handleClick() {
        let tokenvalue = localStorage.getItem("token");
        console.log("value", tokenvalue);
        try {
          const response = await axios.get(
            `${API_URL}asset/view_rf3`,
            (axios.defaults.headers.common["x-access-token"] = tokenvalue)
          );
          console.log("userdata response", response);
          console.log("userdata response", response.data.assetTypedata);
          if (response.status === 200) {
            this.setState({ userdata: response.data.assetTypedata });
          }
          console.log("userdata", this.state.userdata);
        } catch (error) {
          console.log(error);
        }
      }
      openModal() {
        this.setState({
            visible : true
        });
    }

      closeModal() {
        this.setState({
            visible : false
        });
      }
      change = e => {
        this.setState({
          [e.target.name]: e.target.value
        });
      };

      handlechange = (event,id) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        const na = name + "_" + id;
        const name2 = na
        console.log(name2, name, na, value)
        this.setState({
          [na]: value
        });
      };
      getWebsite = () => {
        fetch("/").then(console.log(this.state));
      };
      async onSubmit(event) {
        event.preventDefault();
        console.log(this.state);
        if (this.validator.allValid()) {
          let tokenvalue = localStorage.getItem("token");
          let body = {
            verify_comment: this.state.verify_comment,
            formid: this.props.match.params.id
          };
          console.log("body here", body);
          this.openModal();
          this.setState({
            btnstatus : true
          })
          try {
            const response = await axios.post(
              `${API_URL}rf_form/verify`,
              body,
              (axios.defaults.headers.common["x-access-token"] = tokenvalue)
            );
            console.log(response);
            //   .then(console.log(this.state));

            if (response.data.success) {
              alert(response.data.msg);
                this.props.history.push("/VerifiedRF3");
            } else {
              alert(response.data.msg);
            }
          } catch (error) {
            console.log(error);
          }
        } else {
          this.validator.showMessages();
          // rerender to show messages for the first time
          this.forceUpdate();
        }
      }
      totalshow = e => {
        e.preventDefault();
        let total_sample_sieving = this.state.weight_sample_sieving;
        let total_sample_testing = this.state.weight_sample_testing;
        let total_amount =
          ((total_sample_testing - total_sample_sieving) / total_sample_testing) *
          100;
        this.state.total = total_amount;
        console.log(total_amount);
        this.setState({
          amount_status: true
        });
      };
      render() {
             // let showmodal;
             let sbmtbtn, btnmsg;
             if(this.state.visible)
             {
               // showmodal = (
               //   <div>
               //     <SuccessModal  
               //          visible={this.state.visible}
               //               width={this.props.width}
               //               height={this.props.height}
               //               effect={this.props.effect}
               //               onClickAway={() => this.closeModal()} />
               //   </div>
               // )
             }
             if(this.state.btnstatus)
             {
               sbmtbtn = (
                 <button
                 class="btn btn-primary"
                 onClick={this.onSubmit.bind(this)}
                 disabled
               >
                 Submit
               </button>
               )
               btnmsg = (
                 <div>
                 <hr />
                 <p>Your Form Has Already Been Submitted.Please Don't Click SUMBIT Button AGAIN!!</p>
                 </div>
               )
             }
             else
             {
               sbmtbtn=(
                 <button
                 class="btn btn-primary"
                 onClick={this.onSubmit.bind(this)}
               >
                 Submit
               </button>
               )
             }
        return (
          <div>
            <div className="skin-blue fixed-layout">
              <div className="page-wrapper">
                <div className="container-fluid">
                  <div className="row page-titles">
