                        <ol class="breadcrumb">
                          <li className="breadcrumb-item">
                            <Link to={process.env.PUBLIC_URL + "/"}>Home</Link>
                          </li>
                          <li
                            className="breadcrumb-item active"
                            aria-current="page"
                          >
                            RF - 03
                          </li>
                        </ol>
                      </nav>
                    </div>
                  </div>
                </div>
                {/*form comtent begin */}

                <div className="product-form-upper">
                  <div className="container">
                    <div className="below-custom-form">
                      <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                          {/* <RfButtonLink /> */}

                          <hr />
                        </div>

                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                          <p style={{ textAlign: "center" }}>
                            RF-3 Verification of dimensions of concrete moulds
                          </p>
                          <hr />
                        </div>
                      </div>
                      <form className="custom-content-form">
                        <div className="form-row">
                          <div class="form-group col-md-6">
                            <div class="form-group row">
                              <label
                                for="inputPassword"
                                class="col-sm-2 col-form-label"
                              >
                                Plant
                              </label>
                              <div class="col-sm-10">
                               <input type="text" className="form-control" value={this.state.plant} name="plant" disabled />
                              </div>
                            </div>
                          </div>
                          <div class="form-group col-md-6">
                            <div class="form-group row">
                              <label
                                for="inputPassword"
                                class="col-sm-2 col-form-label"
                              >
                                date
                              </label>
                              <div class="col-sm-10">
                              <input type="text" className="form-control" value={this.state.date} name="date" disabled />
                              </div>
                            </div>
                          </div>
                        </div>
                      </form>
                    </div>
                    {/*test form */}
                    <div className="below-custom-form">
                      <form className="custom-content-form">
                        <div className="form-row">
                          <div class="form-group col-md-12">
                          <table className="table table-hover table-bordered ">
                              <thead className="bg-info custom-thead-color">
                                <tr>
                                <th scope="col">S.No.</th>
                                  <th scope="col">Mould Number</th>
                                  <th scope="colgroup" colspan="2">Length(L1 |L1)</th>
                                  <th scope="colgroup" colspan="2">Breadth(B2 | B2)</th>
                                  <th scope="col" colspan="2">Height (h1 | h2)</th>
                                  <th scope="col" colspan="2">Diagonal (D1 | D2)</th>
                                  <th scope="col">Status</th>
                                </tr>
                              </thead>
                              <tbody>
                                {this.state.userdata.length ? (
                                  this.state.userdata
                                    .map(function(item, id) {
                                      {/* let newid = {id} */}
                                      return (
                                        <tr key={id}>
                                          <th>#</th>
                                          <th scope="row">
                                         {item.asset_description}
                                          </th>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="len"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}
                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="length"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="bre"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="breadth"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="h1"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="h2"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="diagonal1"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td>
                                            <input
                                              type="text"
                                              className="form-control"
                                              name="diagonal2"
                                              value={item.id}
                                              onChange={e => this.handlechange(e,id)}

                                              disabled
                                            />
                                          </td>
                                          <td className="customrf2status">  <select className="form-control" name="status1" value={item.id} onChange={e => this.handlechange(e, id)}>
                                                  <option value="None">Choose...</option>            
                                                  <option value="OK">OK</option>
                                                  <option value="Need Repair">Need Repair</option>
                                                  <option value="Need Replacement">Need Replacement</option>
                                            </select></td>
                                        </tr>
                                      );
                                    }, this)
                                    .reverse()
                                ) : (
                                  <span>Data is loading....</span>
                                )}
                              </tbody>
                            </table>
                            {/* <table class="table table-bordered">
