import React, { Component } from "react";
import "../Test/Test.css";
import { Link, NavLink } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
import Moment from "react-moment";
export class EmployeeMaster extends Component {
  constructor(props) {
    super(props);
    this.state = {
   ft_emp_code : "",
   ft_created_date : "",
   ft_name : "",
   ft_contact : "",
   ft_login : "",
   ft_password : ""
    };
    this.validator = new SimpleReactValidator();
  }
  componentWillMount = () => {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();

    if (dd < 10) {
      dd = "0" + dd;
    }

    if (mm < 10) {
      mm = "0" + mm;
    }

    today = yyyy + "/" + mm + "/" + dd;
    console.log("date here", today);
    this.state.ft_created_date = today;
    console.log(this.state.ft_created_date);
  };
  componentDidMount() {
    this.asset();
  }
  async asset() {
    let tokenvalue = localStorage.getItem("token");
    console.log("value", tokenvalue);
    try {
      const response = await axios.get(
        `${API_URL}asset_type/view`,
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
      );

      console.log(response.data);
      if (response.data.success) {
        await this.setState({
          userdata: response.data.assetTypedata
        });
      }
    } catch (error) {
      console.log(error.response);
    }
  }

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getWebsite = () => {
    fetch("/");
  };
  async onSubmit(e) {
    e.preventDefault();
    console.log(this.state);
    if (this.validator.allValid()) {
      let tokenvalue = localStorage.getItem("token");
      let body = {
        ft_emp_code : this.state.ft_emp_code,
   ft_create_date : this.state.ft_created_date,
   ft_name : this.state.ft_name,
   ft_contact : this.state.ft_contact,
   ft_login : this.state.ft_login,
   ft_password : this.state.ft_password
      };
      //   this.props.history.push("/ViewAssetPlant");
      console.log("body state hre", body);
      try {
        const response = await axios.post(
          `${API_URL}master/ft_add`,
          body,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        );

        console.log(response);
        // this.props.history.push("/ViewArea");
        //   .then(console.log(this.state));
        // .then(
        //   function(response) {
        //     //console.log(error);
        if (response.data.success) {
          alert(response.data.msg);
          this.props.history.push("/EmployeeView");
        } else {
          alert(response.data.msg);
        }
        //   }.bind(this)
        // )
        // .catch(error => {
        //   // alert(error.response.data.msg)
        //   console.log(error.response);
        // });
      } catch (error) {
        console.log(error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
  }
  render() {
    return (
      <div className="skin-blue fixed-layout">
        <div className="page-wrapper">
          <div className="container-fluid">
            <div className="row page-titles">
              <div className="col-md-5 align-self-center">
                {/* <h4 className="text-themecolor">Forms</h4> */}
                <nav aria-label="breadcrumb">
                  <ol class="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to="/">Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                      Master Employee
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>

          {/*Form content begin */}

          <div className="product-form-upper">
            <div className="container">
              <div className="below-custom-form below-custom-blog-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Master Employee</h3>
                    <hr />
                    <br />
                  </div>
                </div>

                <form className="custom-content-form">
                  <div class="form-row">
                    <div className="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputSubcategory"
                          className="col-sm-2 col-form-label"
                        >
                        FT Employee Code 
                        </label>
                        <div class="col-sm-10">
                          <input
                            type="text"
                            className="form-control"
                            value={this.state.ft_emp_code}
                            name="ft_emp_code"
                            onChange={e => this.change(e)}
                          />
                        </div>
                      </div>
                    </div>

                    <div className="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputSubcategory"
                          className="col-sm-2 col-form-label"
                        >
                         Created On 
                        </label>
                        <div class="col-sm-10">
                          {this.state.ft_created_date}
                        </div>
                      </div>
                    </div>
                  </div>

                      <div class="form-row">
                    <div className="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputSubcategory"
                          className="col-sm-2 col-form-label"
                        >
                         FT Name
                        </label>
                        <div class="col-sm-10">
                          <input
                            type="text"
                            className="form-control"
                            value={this.state.ft_name}
                            name="ft_name"
                            onChange={e => this.change(e)}
                          />
                        </div>
                      </div>
                    </div>

                    <div className="form-group col-md-6">
                      <div class="form-group row">
                        <label
                          for="inputSubcategory"
                          className="col-sm-2 col-form-label"
                        >
                        Contact Number
                        </label>
