import React, { Component } from "react";
import "./Zone.css";
import { Link, Redirect } from "react-router-dom";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
export class ZoneLoc extends Component {
  constructor() {
    super();
    this.state = {
      userdata: [],
      detailsdata: [],
      currentPage: 1,
      todosPerPage: 10
    };
    this.handlepagenumber = this.handlepagenumber.bind(this);
    this.handleCheck = this.handleCheck.bind(this);
    this.detailCheck = this.detailCheck.bind(this);
  }
  handlepagenumber(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }
  handleCheck(todo) {
    console.log(todo.zone_id);
    let sitemeet = todo.zone_id;
    if (window.confirm("Are You Sure You Want To Delete This Zone?")) {
      axios
        .get(
          `${API_URL}zone/delete?id=${sitemeet}`,
          (axios.defaults.headers.common["authorization"] =
            "Bearer " + authService.getToken())
        )
        .then(
          function(response) {
            console.log(response.data);
            if (response.data.success === true) {
              this.handleClick();
            } else {
              console.log(response.data.msg);
            }
          }.bind(this)
        )
        .catch(error => {
          console.log(error.response);
        });
    }
  }
  detailCheck(todo) {
    console.log(todo.zone_id);
    let sitemeet = todo.zone_id;
    fetch(`${API_URL}zone/detail?id=${sitemeet}`, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + this.state.token,
        "Content-Type": "application/json"
      }
    }).then(res => {
      res
        .json()
        .then(response => {
          console.log(response.zonedata);
          this.setState({ detailsdata: response.zonedata });
          // this.parseJSON(this.state)
        })
        .catch(error => {
          console.log(error.response);
        });
    });
  }

  componentDidMount() {
    this.handleClick();
  }
  async handleClick() {
    let tokenvalue = localStorage.getItem("token");
    console.log('value', tokenvalue)
    try {
      const response = await axios.get(
        `${API_URL}region/view`,
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
      );

      console.log(response);
      if (response.data.success) {
        this.setState({ userdata: response.data.Regiondata });
      }
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    const { todos, currentPage, todosPerPage } = this.state;

    let renderTodos;

    // Logic for displaying todos
    const indexOfLastTodo = currentPage * todosPerPage;
    const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
    const currentTodos = this.state.userdata.slice(
      indexOfFirstTodo,
      indexOfLastTodo
    );

    let zonedatatable;
    let serial = 0;
    if (this.state.userdata.length === 0) {
      renderTodos = (
        <div>
          <br />
          <center>
            <h4>
              <strong>There is No Region Addedd Yet.</strong>
            </h4>
          </center>
        </div>
      );
    } else {
      renderTodos = currentTodos.map((todo, index) => {
        return (
          <tr key={index}>
            {/* <th scope="row">{++serial}</th> */}
            <td>{todo.region_name || "NO DATA"}</td>
            {/* <td>
                      <Link to ={process.env.PUBLIC_URL +`/EditZone/${todo.zone_id}`}>
      <span class="tooltip-toggle" aria-label="Edit" tabindex="0">
        <button className="btn btn-warning custom-edit-btn btn-sm"><i class="fa fa-pencil" aria-hidden="true"></i></button>
        </span>
        </Link>
        <span class="tooltip-toggle" aria-label="Suspend" tabindex="0">
           <button  className="btn btn-danger custom-edit-btn btn-sm" onClick={this.handleCheck.bind(this, todo)}><i class="fa fa-trash-o" aria-hidden="true"></i></button>
          </span>

                      </td> */}
          </tr>
        );
      });
    }
    // Logic for displaying page numbers
    const pageNumbers = [];
    for (
      let i = 1;
      i <= Math.ceil(this.state.userdata.length / todosPerPage);
      i++
    ) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <li
          key={number}
          id={number}
          className="active"
          onClick={this.handlepagenumber}
        >
          {number}
        </li>
      );
    });
    return (
      <React.Fragment>
        <div>
          <div className="skin-blue fixed-layout">
            <div className="page-wrapper">
              <div className="container-fluid">
                <div className="row page-titles">
                  <div className="col-md-5 align-self-center">
                    {/* <h4 className="text-themecolor">Forms</h4> */}
                    <nav aria-label="breadcrumb">
                      <ol class="breadcrumb">
                        <li className="breadcrumb-item">
                          <Link to={process.env.PUBLIC_URL + "/"}>Home</Link>
                        </li>
                        <li
                          className="breadcrumb-item active"
                          aria-current="page"
                        >
                          View Region
                        </li>
                      </ol>
                    </nav>
                  </div>
                </div>
              </div>
              <div className="custom-table-here custom-buttons">
                {/* <div className="container">
                        <div className="row">
                            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                <div className="custom-inner-buttons text-center">
                                   <Link to ={process.env.PUBLIC_URL + "/ViewLocations"}> <button type="button" className="btn btn-info">View Location</button></Link>
                                </div>

                            </div>
