import React, { Component } from 'react';
import './Locations.css';
import {Link, withRouter} from 'react-router-dom';
import axios from 'axios';
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
class EditLocation extends Component {
    constructor(props)
    {
        super(props)
        this.state = {
            zonesdata : [],
                id :this.props.match.params.id,
                zone_id : "",
                location_name : "",
                pin: ""


           };
          //  this.onSubmit = this.onSubmit.bind(this);
    }

    componentDidMount ()
    {
      this.handleClick();
      this.zoneClick();
    }
    handleClick () {
      let Editid = this.props.match.params.id;
      console.log(this.props.match.params.id)
        // // let meetupId = items.admin_id;
        // console.log(this.props)
        //     // console.log(meetupId);
        //     let sitemeet = item.admin_id;
      axios.get(`${API_URL}location/detail?id=${Editid}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.locationdata)
            // this.setState({ data:response.locationdata})
            // this.parseJSON(this.state)
            this.setState({location_name:response.data.locationdata[0].location_name})
            this.setState({pin:response.data.locationdata[0].location_pin})
            this.setState({zone_id:response.data.locationdata[0].zone_id})
            this.setState({zone_name:response.data.locationdata[0].zone_name})
          })

      }

     zoneClick () {
          axios.get(`${API_URL}zone/view`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
          .then((response) => { 
                console.log(response.data.zonedata)
                if(response.data.success)
                {
                this.setState({ zonesdata:response.data.zonedata})
                }
                // this.parseJSON(this.state)
              }).catch(error => {
                console.log(error.response);
              })

        }




      change  = e => {
        this.setState ({
          [e.target.name]: e.target.Value
        });
      };
      getWebsite = () =>
      {
          fetch('/');
      }
      onSubmit = e => 
      {

          e.preventDefault();
          console.log(this.state);
          this.setState ({
           id :this.props.match.params.id,
           zone_id : "",
           location_name : "",
           pin: ""

          })
          console.log(this.state)
          axios.post(`${API_URL}location/update`,this.state,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
          .then(function(response){
             if(response.data.success){
            //   console.log(json);
            alert(response.data.msg);
            this.props.history.push('/ViewLocations')
        }
        else{
          console.log(response);
          alert(response.data.msg);
      }
    }.bind(this))
    .catch(error => {
      console.log(error.response);
    })
      };

    //   fileSelectedHandler = e => {     
    //       e.preventDefault();
    //     let files = e.target.files;
    //     console.log('data',files[0]);
    //   }
  render() {
    // const editmeet = this.props.editmeet;
    return (
        <div className="skin-blue fixed-layout">
        <div className="page-wrapper">

             <div className="container-fluid">

               <div className="row page-titles">
                   <div className="col-md-5 align-self-center">
                       {/* <h4 className="text-themecolor">Forms</h4> */}
                       <nav aria-label="breadcrumb">
                            <ol class="breadcrumb">
                                <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                                <li className="breadcrumb-item active" aria-current="page">Edit Location</li>
                            </ol>
                            </nav>
                   </div>

               </div>


            </div>

    {/*Form content begin */}

<div className = "product-form-upper">
    <div className = "container">
    <div className = "below-custom-form">
        <div className = "row">
           <div className = "col-lg-12 col-md-12 col-sm-12 col-xs-12">
                   <h3>Edit Location</h3>
           </div>
        </div>

        <div>
        <form className="custom-content-form" autoComplete = 'no'>

  <div class="form-row">
    <div class="form-group col-md-4">
    <label for="name">Edit Zone Name</label>
    <select className="form-control" name="zone_id" value = {this.state.zone_id} onChange={(e) => this.setState({zone_id: e.target.value})}>

        <option value={this.state.zone_id}>{this.state.zone_name}</option>   
        {
              this.state.zonesdata ?
              this.state.zonesdata.map(function(item, id) {
                return(
        <option value={item.zone_id} key ={id}>{item.zone_name}</option>
    )
              }, this
      )
              :
              <span>Data is loading....</span>
            }
    </select>
    </div>
    <div class="form-group col-md-4">
    <label for="adminname">Edit Location Name</label><br />              
     <input type="text" className = "form-control" placeholder="neo404" name="location_name" value = {this.state.location_name} onChange={(e) => this.setState({location_name: e.target.value})} />
    </div>
    <div class="form-group col-md-4">
    <label for="adminname">Edit Pin</label><br />              
     <input type="text" className = "form-control" placeholder="neo404" name="pin" value = {this.state.pin} onChange={(e) => this.setState({pin: e.target.value})} />
    </div>
  </div>

  <button class="btn btn-primary" onClick={e => this.onSubmit(e)}>Submit</button>

</form>
</div>

    </div>
    </div>
</div>






        </div>
        </div>

    )
  }
}
export default withRouter(EditLocation)
