import React, { Component } from "react";
import { Link, Redirect, withRouter, NavLink } from "react-router-dom";
import "./Test.css";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
import { SuccessModal } from "./SuccessModal";

const initialState = {
  btnstatus : false,
  total_average_score: "",
  supplier: "",
  source: "",
  sample_location: "",
  weight: "",
  agg_type: "",
  weight_balance: "",
  weight_sample_testing: "",
  weight_sample_sieving: "",
  material_passing: "",
  pto_comment: "",
  total: "",
  testing_quantity: "",
  plant: "",
  total_material_retained1: "",
  total_material_retained2: "",
  total_material_retained3: "",
  s_t1: "",
  s_t2: "",
  s_t3: "",
  s_t4: "",
  s_t5: "",
  s_t6: ""
};

export class FinenessTest extends Component {
  constructor(props) {
    super(props);
    this.state = {
      initialState,
      formstatus: false,
      variablestatus: false,
      amount_status: false,
      _isMounted : false,
      uppercardstatus : true
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.validator = new SimpleReactValidator();
    this.showformhandle = this.showformhandle.bind(this);
  }
  componentWillMount = () => {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();

    if (dd < 10) {
      dd = "0" + dd;
    }

    if (mm < 10) {
      mm = "0" + mm;
    }

    today = mm + "/" + dd + "/" + yyyy;
    console.log("date here", today);
    this.state.date = today;
    console.log(this.state.date);

    this.setState({
      _isMounted : true
    })
  };

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getInfo = async () => {
    try {
      let vendor_name = this.state.query;
      const response = await axios.get(
        `${API_URL}master/vendor_search?VENDOR_NAME=${vendor_name}`,
        (axios.defaults.headers.common["x-access-token"] = localStorage.getItem(
          "token"
        ))
      );
      // console.log(response.data.User);
      //console.log(this.state.supl);
      console.log("response hwew", response);

      if (response.data.success) {
        await this.setState({
          supl: response.data.VENDOR_NAME
        });
        console.log(this.state.supl);
      }
      // this.parseJSON(this.state)
    } catch (error) {
      console.log(error);
    }
  };
  openModal() {
    this.setState({
        visible : true
    });
}

  closeModal() {
    this.setState({
        visible : false
    });
  }
  handleInputChange = e => {
    console.log(e.target.value);
    this.setState(
      {
        query: e.target.value
      },
      () => {
        if (this.state.query && this.state.query.length > 1) {
          if (this.state.query.length % 2 === 0) {
            if(this.state._isMounted)
            {
            this.getInfo();
            }
          }
        } else if (!this.state.query) {
        }
      }
    );
  };
  showformhandle() {
    const doesShow = this.state.formstatus;
    this.setState({ formstatus: !doesShow });
    this.setState(initialState);
  }
  getWebsite = () => {
    fetch("/").then(console.log(this.state));
  };
  async onSubmit(event) {
    event.preventDefault();
    console.log(this.state);
    if (this.validator.allValid()) {
      let tokenvalue = localStorage.getItem("token");
      let type;
      if (this.state.formstatus) {
        type = "RF_18";
      } else {
        type = "RF_17";
      }
      let body = {
        supplier: this.state.query,
        source: this.state.source,
        sample_location: this.state.sample_location,
        weight: this.state.weight,
        weight_balance: this.state.weight_balance,
        weight_sample_testing: this.state.weight_sample_testing,
        weight_sample_sieving: this.state.weight_sample_sieving,
        material_passing: this.state.total,
        pto_comment: this.state.pto_comment,
        plant: localStorage.getItem("plant_name"),
        date: this.state.date,
        type: type,
        total_material_retained1: this.state.total_material_retained1,
        total_material_retained2: this.state.total_material_retained2,
        total_material_retained3: this.state.total_material_retained3,
        total_average_score: this.state.total_average_score,

        s_t1: this.state.s_t1,
        s_t2: this.state.s_t2,
        s_t3: this.state.s_t3,
        s_t4: this.state.s_t4,
        s_t5: this.state.s_t5,
        s_t6: this.state.s_t6
      };
      console.log("body here", body);
      this.openModal();
      this.setState({
        btnstatus : true
      })
      try {
        const response = await axios.post(
          `${API_URL}rf_form/submit`,
          body,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        );
        console.log(response);
        //   .then(console.log(this.state));

        if (response.data === "success") {
          // alert(response.data);
          this.props.history.push("/VewFineness");
        } else {
          alert(response.data);
        }
      } catch (error) {
        console.log(error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
  }
  total_material_retained = e => {
    function isNumber(n) {
      return !isNaN(parseFloat(n)) && isFinite(n);
    }
    e.preventDefault();
    if (
      this.state.s_t1 != 0 &&
      isNumber(this.state.s_t1) &&
      isNumber(this.state.s_t4)
    ) {
      let C = (this.state.s_t4 / this.state.s_t1) * 100;
      this.state.total_material_retained1 = C.toFixed(2);
      console.log(C);
    }
    // else
    // alert('please give correct input')
    //vd2
    if (
      this.state.s_t2 != 0 &&
      isNumber(this.state.s_t2) &&
      isNumber(this.state.s_t5)
    ) {
      let C2 = (this.state.s_t5 / this.state.s_t2) * 100;
      this.state.total_material_retained2 = C2.toFixed(2);
      console.log(C2);
    }
    // else
    // alert('please give correct input')
    //fine3
    if (
      this.state.s_t3 != 0 &&
      isNumber(this.state.s_t3) &&
      isNumber(this.state.s_t6)
    ) {
      let C3 = (this.state.s_t6 / this.state.s_t3) * 100;
      this.state.total_material_retained3 = C3.toFixed(2);
      console.log(C3);
    }
    // else
    // alert('please give correct input')

    this.setState({
      amount_status: true
    });
  };
  total_average_fine = e => {
    e.preventDefault();
    let average_score;
    this.state.total_average_score = 0;
    if (
      this.state.total_material_retained1 != 0 &&
      this.state.total_material_retained2 == 0 &&
      this.state.total_material_retained3 == 0
    ) {
      this.state.total_average_score = this.state.total_material_retained1.toFixed(
        2
      );
    } else if (
      this.state.total_material_retained1 == 0 &&
      this.state.total_material_retained2 != 0 &&
      this.state.total_material_retained3 == 0
    ) {
      this.state.total_average_score = this.state.total_material_retained2.toFixed(
        2
      );
    } else if (
      this.state.total_material_retained1 == 0 &&
      this.state.total_material_retained2 == 0 &&
      this.state.total_material_retained3 != 0
    ) {
      this.state.total_average_score = this.state.total_material_retained3.toFixed(
        2
      );
    } else {
      average_score =
        (+this.state.total_material_retained1 +
          +this.state.total_material_retained2 +
          +this.state.total_material_retained3) /
        3;
      this.state.total_average_score = average_score.toFixed(2);
    }

    this.setState({
      amount_status: true
    });
  };
  componentWillUnmount() {
    this.state._isMounted = false
  }
  render() {
         // let showmodal;
         let sbmtbtn, btnmsg, uppercardinfo17, uppercardinfo18;
         if(this.state.uppercardstatus)
         {
           uppercardinfo17 = (
             <div>
               <form className="custom-content-form">
            <div className="form-row">
              <div class="form-group col-md-6">
                <div class="form-group row">
                  <label for="inputPassword" class="col-sm-2 col-form-label">
                    Plant
                  </label>
                  <div class="col-sm-10">
                    {localStorage.getItem("plant_name")}
                  </div>
                </div>
              </div>
              <div class="form-group col-md-6">
                <div class="form-group row">
                  <label for="inputPassword" class="col-sm-2 col-form-label">
                    date
                  </label>
                  <div class="col-sm-10">{this.state.date}</div>
                </div>
              </div>
              <div class="form-group col-md-6">
                <div class="form-group row">
                  <label for="inputPassword" class="col-sm-2 col-form-label">
                    Supplier
                  </label>
                  <div class="col-sm-10">
                    <input
                      list="browsers"
                      name="query"
                      value={this.state.query}
                      onChange={e => this.handleInputChange(e)}
                      className="form-control"
                    />

                    <datalist id="browsers">
                      {this.state.supl ? (
