import React, { Component } from 'react'
import "../Test/Test.css";
import { Link, NavLink } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
import Moment from "react-moment";
export class SiteMaster extends Component {
    constructor(props) {
        super(props);
        this.state = {
      vendor_site : ""
        };
        this.validator = new SimpleReactValidator();
      }
      componentDidMount() {
        this.asset();
      }
      async asset() {
        let tokenvalue = localStorage.getItem("token");
        console.log("value", tokenvalue);
        try {
          const response = await axios.get(
            `${API_URL}asset_type/view`,
            (axios.defaults.headers.common["x-access-token"] = tokenvalue)
          );

          console.log(response.data);
          if (response.data.success) {
            await this.setState({
              userdata: response.data.assetTypedata
            });
          }
        } catch (error) {
          console.log(error.response);
        }
      }

      change = e => {
        this.setState({
          [e.target.name]: e.target.value
        });
      };
      getWebsite = () => {
        fetch("/");
      };
      async onSubmit(e) {
        e.preventDefault();
        console.log(this.state);
        if (this.validator.allValid()) {
          let tokenvalue = localStorage.getItem("token");
          let body = {
            VENDOR_SITE : this.state.vendor_site
          };
          //   this.props.history.push("/ViewAssetPlant");
          console.log("body state hre", body);
          try {
            const response = await axios.post(
              `${API_URL}master/vendor_plant`,
              body,
              (axios.defaults.headers.common["x-access-token"] = tokenvalue)
            );

            console.log(response);
            // this.props.history.push("/ViewArea");
            //   .then(console.log(this.state));
            // .then(
            //   function(response) {
            //     //console.log(error);
            if (response.data.success) {
              alert(response.data.msg);
              this.props.history.push("/SiteView");
            } else {
              alert(response.data.msg);
            }
            //   }.bind(this)
            // )
            // .catch(error => {
            //   // alert(error.response.data.msg)
            //   console.log(error.response);
            // });
          } catch (error) {
            console.log(error);
          }
        } else {
          this.validator.showMessages();
          // rerender to show messages for the first time
          this.forceUpdate();
        }
      }
      render() {
        return (
          <div className="skin-blue fixed-layout">
            <div className="page-wrapper">
              <div className="container-fluid">
                <div className="row page-titles">
                  <div className="col-md-5 align-self-center">
                    {/* <h4 className="text-themecolor">Forms</h4> */}
                    <nav aria-label="breadcrumb">
                      <ol class="breadcrumb">
                        <li className="breadcrumb-item">
                          <Link to="/">Home</Link>
                        </li>
                        <li className="breadcrumb-item active" aria-current="page">
                          Master Employee
                        </li>
                      </ol>
                    </nav>
                  </div>
                </div>
              </div>

              {/*Form content begin */}

              <div className="product-form-upper">
                <div className="container">
                  <div className="below-custom-form below-custom-blog-form">
                    <div className="row">
                      <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <h3>Master Site</h3>
                        <hr />
                        <br />
                      </div>
                    </div>

                    <form className="custom-content-form">
                      <div class="form-row">
                        <div className="form-group col-md-12">
                          <div class="form-group row">
                            <label
                              for="inputSubcategory"
                              className="col-sm-2 col-form-label"
                            >
                            Vendor Site
                            </label>
                            <div class="col-sm-10">
                              <input
                                type="text"
                                className="form-control"
                                value={this.state.vendor_site}
                                name="vendor_site"
                                onChange={e => this.change(e)}
                              />
                            </div>
                          </div>
                        </div>


                      </div>


                      <button
                        class="btn btn-primary"
                        onClick={e => this.onSubmit(e)}
                      >
                        Submit
                      </button>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      }
    }

export default SiteMaster
