import React, { Component } from 'react'
import "../Test/Test.css";
import { Link, NavLink } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../../services/url";
import ReactTable from 'react-table'

export class VendorView extends Component {
  constructor() {
    super()
    this.state = {
      vendorsList: []
    }
  }

  async componentDidMount() {
    const tokenvalue = localStorage.getItem('token')
    try {
      const response = await axios.get(
        `${API_URL}master/vendor`,
        (axios.defaults.headers.common['x-access-token'] = tokenvalue)
      )
      console.log(response)
      if (response.status === 200) {
        this.setState({ vendorsList: response.data.VENDOR_NAME })
      }
    } catch (error) {
      console.log(error)
    }
  }

  render() {
    const columns = [
      {
        Header: 'Vendor Code',
        accessor: 'VENDOR_CODE',
        sortable: false
      },
      {
        Header: 'Vendor Name',
        accessor: 'VENDOR_NAME',
        sortable: true
      },
      {
        Header: 'Vendor Site',
        accessor: 'VENDOR_SITE',
        sortable: false
      },
      {
        Header: 'PO Number',
        accessor: 'PO_NUMBER',
        sortable: false
      },
      {
        Header: 'Item Code',
        accessor: 'ITEM_CODE',
        sortable: false
      },
      {
        Header: 'Item Description',
        accessor: 'ITEM_DESCRIPTION',
        sortable: false
      },
      {
        Header: 'Organization Name',
        accessor: 'ORGANIZATION_NAME',
        sortable: false
      },
      {
        Header: 'Organization Code',
        accessor: 'ORGANIZATION_CODE',
        sortable: false
      },
      {
        Header: 'PO Authorization Status',
        accessor: 'PO_AUTHORIZATION_STATUS',
        sortable: false
      },
      {
        Header: 'PO From Date',
        accessor: 'PO_FROM_DATE',
        sortable: false
      },
      {
        Header: 'PO To Date',
        accessor: 'PO_TO_DATE',
        sortable: false
      },
      {
        Header: 'Creation Date',
        accessor: 'CREATION_DATE',
        sortable: false
      },
      {
        Header: 'Update Date',
        accessor: 'LAST_UPDATE_DATE',
        sortable: false
      }
    ]

    return (
      <div className='skin-blue fixed-layout'>
        <div className='page-wrapper'>
          <div className='container-fluid'>
            <div className='row page-titles'>
              <div className='col-md-5 align-self-center'>
                <nav aria-label='breadcrumb'>
                  <ol class='breadcrumb'>
                    <li className='breadcrumb-item'>
                      <Link to={process.env.PUBLIC_URL + '/'}>Home</Link>
                    </li>
                    <li className='breadcrumb-item active' aria-current='page'>
                      Plant Vendor's
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>
          <div className='container my-3 w-100'>
            <NavLink to='LocalVendorView' className='mb-3' style={{ float: 'right', marginRight: '1.75rem' }}>
              <button className='btn btn-info'>View Local Vendor's</button>
            </NavLink>
          </div>
          <div className='container'>
            <ReactTable
              columns={columns}
              data={this.state.vendorsList}
              className='table table-light -striped -highlight table-responsive text-center'
              headerClassName='bg-info'
              showPagination
              defaultPageSize={15}
              id='customersTable'
              noDataText='Data not found.'
              ref={(r) => {
                this.selectTable = r
              }}
            />
          </div>
        </div>
      </div>
    )
  }
}

export default VendorView
