import React, { Component } from "react";
import "./Admin.css";
import { Link, withRouter, Redirect } from "react-router-dom";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
class Admin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      nameError: "",
      adminname: "",
      adminnameError: "",
      password: "",
      passwordError: "",
      email: "",
      emailError: "",
      mobile1: "",
      mobile1Error: "",
      mobile2: ""
    };
    this.onSubmit = this.onSubmit.bind(this);
  }
  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getWebsite = () => {
    fetch("/");
  };
  validate = () => {
    let isError = false;
    const errors = {};

    if (this.state.adminname.length < 2) {
      isError = true;
      errors.adminnameError =
        "Admin User Name Should be Filled and atleast of 2 characters.";
    }

    if (isError) {
      this.setState({
        ...this.state,
        ...errors
      });
    }
    return isError;
  };
  async onSubmit(event) {
    event.preventDefault();
    console.log(this.state);
    const err = this.validate();

    if (!err) {
      //   this.setState ({
      //     name : "",
      //     adminname : "",
      //     password : "",
      //     email : "",
      //     mobile1 : "",
      //     mobile2 : ""
      //   })e.preventDefault();
      console.log(this.state);
      axios
        .post(
          `${API_URL}admin/add`,
          this.state,
          (axios.defaults.headers.common["authorization"] =
            "Bearer " + authService.getToken())
        )
        //   .then(console.log(this.state));
        .then(
          function(response) {
            console.log(response);
            if (response.data.success) {
              alert(response.data.msg);
              this.props.history.push("/AdminEmp");
            } else {
              alert(response.data.msg);
            }
          }.bind(this)
        )
        .catch(error => {
          console.log(error.response);
        });

      //       fetch('admin/add',{
      //         method : "POST",
      //         headers : {
      //          'Authorization': 'Bearer ' + this.state.token,
      //         "Content-Type" : "application/json"
      //         },
      //         body: JSON.stringify(this.state)
      //       })
      //         //   .then(console.log(this.state));
      //     .then(function(response){
      //         return response.json();})
      //     .then(function(json){
      //          if(json.success===true){
      //         //   console.log(json);
      //         this.setState ({
      //             name : "",
      //             adminname : "",
      //             password : "",
      //             email : "",
      //             mobile1 : "",
      //             mobile2 : ""
      //           })
      //         alert("your data has been submitted");
      //         this.props.history.push('/AdminEmp');
      //         return <Redirect to ='/AdminEmp'/>
      //     }
      //     else{
      //         alert(json.msg);
      //       console.log(json);
      //   }
      // }.bind(this))
    }
  }

  //   fileSelectedHandler = e => {
  //       e.preventDefault();
  //     let files = e.target.files;
  //     console.log('data',files[0]);
  //   }
  render() {
    return (
      <div className="skin-blue fixed-layout">
        <div className="page-wrapper">
          <div className="container-fluid">
            <div className="row page-titles">
              <div className="col-md-5 align-self-center">
                {/* <h4 className="text-themecolor">Forms</h4> */}
                <nav aria-label="breadcrumb">
                  <ol class="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to={process.env.PUBLIC_URL + "/"}>Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                      Add Admin
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>

          {/*Form content begin */}

          <div className="product-form-upper">
            <div className="container">
              <div className="below-custom-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Add Admin</h3>
                  </div>
                </div>
                <form className="custom-content-form" autoComplete="no">
                  <div class="form-row">
                    <div class="form-group col-md-6">
                      <label for="inputCategory">Enter Name*</label>
                      <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={this.state.name}
                        onChange={e => this.change(e)}
                        errorText={this.state.nameError}
                      />
                    </div>
                    <div class="form-group col-md-6">
                      <label for="inputSubcategory">Enter User Name*</label>
                      <br />
                      <input
                        type="text"
                        className="form-control"
                        name="adminname"
                        value={this.state.adminname}
                        onChange={e => this.change(e)}
                        onError={this.state.adminnameError}
                      />
                    </div>
                    <div class="form-group col-md-6">
                      <label for="inputSubcategory">Enter Mobile Number*</label>
                      <br />
                      <input
                        type="text"
                        className="form-control"
                        name="mobile1"
                        value={this.state.mobile1}
                        onChange={e => this.change(e)}
                        errorText={this.state.mobile1Error}
                      />
                    </div>
                    <div class="form-group col-md-6">
                      <label for="inputSubcategory">
                        Enter Alternate Mobile Number{" "}
                      </label>
                      <br />
                      <input
                        type="text"
                        className="form-control"
                        name="mobile2"
                        value={this.state.mobile2}
                        onChange={e => this.change(e)}
                      />
                    </div>
                    <div class="form-group col-md-6">
                      <label for="inputSubcategory">Enter User Email*</label>
                      <br />
                      <input
                        type="text"
                        className="form-control"
                        name="email"
                        value={this.state.email}
                        onChange={e => this.change(e)}
                        errorText={this.state.emailError}
                      />
                    </div>
                    <div class="form-group col-md-6">
                      <label for="inputSubcategory">Enter Password*</label>
                      <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={this.state.password}
                        onChange={e => this.change(e)}
                        errorText={this.state.passwordError}
                      />
                    </div>
                    <small>* This Fields are mendatory. </small>
                  </div>

                  <button
                    class="btn btn-primary"
                    onClick={this.onSubmit.bind(this)}
                  >
                    Submit
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(Admin);
