import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import "./Area.css";
import axios from "axios";
import { API_URL } from "../../services/url";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import PropTypes from "prop-types";
import SimpleReactValidator from "simple-react-validator";
export class Area extends Component {
  constructor(props) {
    super(props);
    this.state = {
      area_name: "",
      region_id: "",
      region:[]
    };
    this.validator = new SimpleReactValidator();
  }
  componentDidMount() {
    this.handleClick();
  }
  async handleClick() {
    let tokenvalue = localStorage.getItem("token");
    try {
      const response = await axios.get(
        `${API_URL}region/view`,
        (axios.defaults.headers.common["x-access-token"] = tokenvalue)
      );

      console.log(response);
      if (response.data.success) {
        this.setState({ region: response.data.Regiondata });
      }
    } catch (error) {
      console.log(error);
    }
  }
  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  getWebsite = () => {
    fetch("/");
  };
  async onSubmit(e) {
    e.preventDefault();
    let tokenvalue = localStorage.getItem("token");
    console.log(tokenvalue)
    if (this.validator.allValid()) {
      console.log(this.state);
      let body = {
        area: this.state.area_name,
        region_id: this.state.region_id
      };

      console.log("body state hre", body);
      try {
        const response = await axios.post(
          `${API_URL}area/add`,
          body,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        );

        console.log(response);
        if (response.data.success) {
          alert(response.data.msg);
          this.props.history.push("/ViewArea");
        } else {
          alert(response.data.msg);
        }
      } catch (error) {
        console.log(error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
  }

  getCurrentPosition() {
    console.log(this.prop.latitude);
    //   {this.props.getCurrentPosition}
  }
  render() {
    let i = 1;
    return (
      <div className="skin-blue fixed-layout">
        <div className="page-wrapper">
          <div className="container-fluid">
            <div className="row page-titles">
              <div className="col-md-5 align-self-center">
                {/* <h4 className="text-themecolor">Forms</h4> */}
                <nav aria-label="breadcrumb">
                  <ol class="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to="/">Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                      Add Area
                    </li>
                  </ol>
                </nav>
              </div>
            </div>
          </div>

          {/*Form content begin */}

          <div className="product-form-upper">
            <div className="container">
              <div className="below-custom-form below-custom-blog-form">
                <div className="row">
                  <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                    <h3>Add Area</h3>
                    <hr />
                    <br />
                  </div>
                </div>

                <form className="custom-content-form">
                  <div class="form-group row">
                    <label
                      for="inputSubcategory"
                      className="col-sm-2 col-form-label"
                    >
                      Area Name
                    </label>
                    <div class="col-sm-10">
                      <input
                        type="text"
                        className="form-control"
                        value={this.state.area_name}
                        name="area_name"
                        onChange={e => this.change(e)}
                      />
                      <span className="text-danger">
                        {this.validator.message(
                          "Name",
                          this.state.area_name,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>
                  <div class="form-group row">
                    <label
                      for="inputSubcategory"
                      className="col-sm-2 col-form-label"
                    >
                      Under Region
                    </label>
                    <div class="col-sm-10">
                      <select
                        className="form-control"
                        value={this.state.region_id}
                        name="region_id"
                        onChange={e => this.change(e)}
                      >
                         <option>Select One</option>
    {
              this.state.region ?
              this.state.region.map(function(item, id) {
                return(

        <option key ={id} value={item.region_id}>{item.region_name}</option>
      )
              }
      )
              :
              <span>Data is loading....</span>
            }

                      </select>
                      <span className="text-danger">
                        {this.validator.message(
                          "region",
                          this.state.region_id,
                          "required"
                        )}
                      </span>
                    </div>
                  </div>

                  <button
                    class="btn btn-primary"
                    onClick={e => this.onSubmit(e)}
                  >
                    Submit
                  </button>

                  {/* <br />
  <input type="text" className="form-control" value={this.state.deg} name="deg" onChange = {e => this.change(e)}/>
  <br />
  <button class="btn btn-primary" onClick = {deg => this.deg2rad(deg)}>Submit</button> */}
                </form>
                <br />
                {/* <Geolocation 
  lazy 
  render={({getCurrentPosition, fetchingPosition}) => (
    <div>
      <button onClick={getCurrentPosition}>Get Current Position</button>
      <div>Fetching Position: {fetchingPosition}</div>
    </div>
  )}
/> */}
                {/* <Geolocation
  lazy render={({
    fetchingPosition,
    position: { coords: { latitude, longitude } = {} } = {},
    error,
    getCurrentPosition
  }) =>
    <div>
      <button onClick={getCurrentPosition}>Get Position</button>
      {error &&
        <div>
          {error.message}
        </div>}
      <pre>
        latitude: {latitude}
        longitude: {longitude}
      </pre>
      <pre>
        new lo:{this.state.ui_longitude}
        new la:{this.state.ui_latitude}
      </pre>
    </div>}
/> */}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(Area);
