import React, { Component } from 'react';
import './Admin.css';
import {Link} from 'react-router-dom';
import axios from 'axios';
class ViewAdmin extends Component {
    constructor (props) {
        super(props) 
        this.state = {
          data : [],
          detailsdata : [],
          editdata : []
        }
        this.handleCheck = this.handleCheck.bind(this);
        this.detailCheck = this.detailCheck.bind(this);
        // this.editCheck = this.editCheck.bind(this);
        // this.handleInputChange = this.handleInputChange.bind(this);
    }
    // componentWillUpdate(nextProps, nextState) {
    //   localStorage.setItem('token', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhaWQiOjEsImlhdCI6MTU0MDI3MzI4N30.xG68rUe4dadGAprGwvhjb_0BLSs81STXfy2BO1t09Yk')
    // }
    // componentWillMount() {
    //   this.detailCheck();
    //   this.handleCheck();
    // }
    // editbtn(item) {
    //   let editmeet = item.admin_id;
    //   console.log(this.props.editmeet);
    // }
    handleCheck(item) {
      // console.log(item.admin_id);
      let sitemeet = item.admin_id;
      fetch(`admin/delete?admin_id=${sitemeet}`,{
                 method : 'GET',
                 headers : {
                  //  'Authorization': 'Bearer ' + this.state.token,
                   "Content-Type" : "application/json"
               }
               })
               .then(function(response){ 
                       return response.json();})
                   .then(function(json){
                        if(json.success===true){
                       //   console.log(json);
                          this.handleClick();
                       alert("admin has been deleted PLEASE REFRESH THE PAGE");
                   }
                   else{
                     console.log(json);
                 }
                 })
     }

     detailCheck(item) {
      // console.log(item.admin_id);
      let sitemeet = item.admin_id;
      fetch(`admin/edit?id=${sitemeet}`,{
                 method : 'GET',
                 headers : {
                  //  'Authorization': 'Bearer ' + this.state.token,
                   "Content-Type" : "application/json"
               }
               })
               .then((res) => {
                res.json().then((response) => { 
                  console.log(response.admindata)
                  this.setState({ detailsdata:response.admindata})
                  // this.parseJSON(this.state)
                })
              }

            )
     }

    //  editCheck(item) {
    //   console.log(item.admin_id);
    //   let sitemeet = item.admin_id;
    //   fetch(`admin/edit?id=${sitemeet}`,{
    //              method : 'GET',
    //              headers : {
    //               //  'Authorization': 'Bearer ' + this.state.token,
    //                "Content-Type" : "application/json"
    //            }
    //            })
    //            .then((res) => {
    //             res.json().then((response) => { 
    //               console.log(response.admindata)
    //               this.setState({ editdata:response.admindata})
    //               // this.parseJSON(this.state)
    //             })
    //           }

    //         )
    //  }
    //WARNING! To be deprecated in React v17. Use componentDidMount instead.

    componentDidMount ()
    {
      this.handleClick();
    }
    handleClick () {
      fetch('admin/view',{
        method : 'GET',
        headers : {
          // 'Authorization': 'Bearer ' + this.state.token,
          "Content-Type" : "application/json"
      }
      })
        .then((res) => {
          res.json().then((response) => { 
            console.log(response.admindata)
            this.setState({ data:response.admindata})
            // this.parseJSON(this.state)
          })
        }

      )
      }
      // handleInputChange(e){
      //   console.log('handleinpuchage funcion');
      //   const target = e.target;
      //   const value = target.value;
      //   const name = target.name;

      //   this.setState({
      //     [name]: value
      //   });
      // }
        render() {
            return (
                <React.Fragment>
                <div>
                <div className="skin-blue fixed-layout">
            <div className="page-wrapper">

                 <div className="container-fluid">

                   <div className="row page-titles">
                       <div className="col-md-5 align-self-center">
                           <nav aria-label="breadcrumb">
                                <ol class="breadcrumb">
                                    <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                                    <li className="breadcrumb-item active" aria-current="page">View Admin</li>
                                </ol>
                                </nav>
                       </div>

                   </div>


                </div>
               <div className="custom-table-here">
                  <div className="container">
                    <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
    <table className="table table-hover table-bordered ">
      <thead className="bg-info custom-thead-color">
        <tr>
          <th scope="col">Full Name</th>
          <th scope="col">Admin Name</th>
          <th scope="col">Email</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody>
      {
              this.state.data.length ?
              this.state.data.map(function(item, id) {
                return(

        <tr key = {id}>
          <th scope="row">{item.full_name}</th>
          <td>{item.adminname}</td> 
          <td>{item.email}</td> 
          <td>
    <Link to ={process.env.PUBLIC_URL +`/EditAdmin/${item.admin_id}`}> <button className="btn btn-warning custom-edit-btn btn-sm" data-toggle="tooltip" data-placement="top" title="Tooltip on top"><i class="fa fa-pencil" aria-hidden="true"></i>&nbsp; &nbsp; Edit</button></Link>
          <button  className="btn btn-danger custom-edit-btn btn-sm" onClick={this.handleCheck.bind(this, item)}><i class="fa fa-trash-o" aria-hidden="true"></i>&nbsp;&nbsp;Delete</button>
          <button className="btn btn-success custom-edit-btn btn-sm" onClick={this.detailCheck.bind(this, item)} data-toggle="modal" data-target=".bd-example-modal-lg"><i class="fa fa-eye" aria-hidden="true"></i>&nbsp; &nbsp; Details</button>
          </td>
        </tr>
      )
              }, this
      )
              :
              <span>Data is loading....</span>
            }
      </tbody>
    </table>
    {/* <button  className="btn btn-danger custom-edit-btn btn-sm" onClick={this.handleCheck}>Delete</button> */}

        {/* details modal here */} 
        <div class="modal fade bd-example-modal-lg" tabindex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg">
    <div class="modal-content">
    <table className="table table-hover table-bordered ">
      <thead className="bg-info custom-thead-color">
        <tr>
          <th scope="col">Full Name</th>
          <th scope="col">Admin Name</th>
          <th scope="col">Email</th>
        </tr>
      </thead>
      <tbody>
      {
              this.state.detailsdata ?
              this.state.detailsdata.map(function(item, id) {
                return(

        <tr key = {id}>
          <th scope="row">{item.full_name}</th>
          <td>{item.adminname}</td> 
          <td>{item.email}</td> 
        </tr>
      )
              }, this
      )
              :
              <span>Data is loading....</span>
            }
      </tbody>
    </table>
    <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>

  </div>
</div>    



                    </div>
                    </div>
                  </div>  

               </div>
               </div>
               </div>
                </div>
                </React.Fragment>
            );
        }
    }

export default ViewAdmin;
