import React, { Component } from 'react'
import './Locations.css';
import {Link, withRouter, Redirect} from 'react-router-dom';
import axios from 'axios';
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
export class AddLocation extends Component {
    constructor () {
        super() 
        this.state = {
          location : "",
            pin : "",
          userdata : []
        }
        this.onSubmit = this.onSubmit.bind(this);
    }
    componentWillMount ()
    {
      this.handleClick();
    }
    handleClick () {
      axios.get(`${API_URL}zone/view`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then((response) => { 
            console.log(response.data.zonedata)
            this.setState({ userdata:response.data.zonedata})
            // this.parseJSON(this.state)
          })
          .catch(error => {
            console.log(error.response);
          })
      }
      change  = e => {
        this.setState ({
          [e.target.name]: e.target.value
        });
      };
      getWebsite = () =>
      {
          fetch('/').then(console.log(this.state));
      }
      onSubmit  = (event) => 
      {
          event.preventDefault();
          console.log(this.state);
          axios.post(`${API_URL}location/add`, this.state,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
          //   .then(console.log(this.state));
        .then(function(response){ 
           console.log(response);
           if(response.data.success === true)
           {
             alert('Location added successfully')
          this.props.history.push('/ViewLocations')
           }
           else{
             alert(response.data.msg)
           }
        }.bind(this))
        .catch(error => {
            console.log(error.response);
          })
}

  render() {
    return (
        <div className="skin-blue fixed-layout">
        <div className="page-wrapper">

             <div className="container-fluid">

               <div className="row page-titles">
                   <div className="col-md-5 align-self-center">
                       {/* <h4 className="text-themecolor">Forms</h4> */}
                       <nav aria-label="breadcrumb">
                            <ol class="breadcrumb">
                                <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL +'/'}>Home</Link></li>
                                <li className="breadcrumb-item active" aria-current="page">Location Form</li>
                            </ol>
                            </nav>
                   </div>

               </div>


            </div>

    {/*Form content begin */}

<div className = "product-form-upper">
    <div className = "container">
    <div className = "below-custom-form">
        <div className = "row">
           <div className = "col-lg-12 col-md-12 col-sm-12 col-xs-12">
                   <h3>Add Location</h3>
           </div>
        </div>
        <form className="custom-content-form">
  <div class="form-row">
  <input type="hidden" className="form-control" name="cid" />
  <div class="form-group col-md-4">
    <label for="inputSubcategory">Enter Zone</label>
    <select className="form-control" value={this.state.zone_id} name="zone_id" onChange = {e => this.change(e)}>
    <option>Select One</option>
    {
              this.state.userdata ?
              this.state.userdata.map(function(item, id) {
                return(

        <option key ={id} value={item.zone_id}>{item.zone_name}</option>
      )
              }
      )
              :
              <span>Data is loading....</span>
            }

    </select>
    </div>
    <div class="form-group col-md-4">
    <label for="inputSubcategory">Enter Location</label>
     <input type="text" className = "form-control" name="location" value={this.state.location} onChange = {e => this.change(e)} />
    </div>
    <div class="form-group col-md-4">
    <label for="inputSubcategory">Enter Pin</label>
     <input type="text" className = "form-control" name="pin" value={this.state.pin} onChange = {e => this.change(e)} />
    </div>
  </div>

  <button class="btn btn-primary" onClick = {this.onSubmit.bind(this)}>Submit</button>
</form>
    </div>
    </div>
</div>






        </div>
        </div>

    )
  }
}

export default withRouter(AddLocation)
