import React, { Component } from "react";
import { Link, Redirect, withRouter, NavLink } from "react-router-dom";
import "./Test.css";
import axios from "axios";
import authService from "../../services/auth-service";
import axiosService from "../../services/axios-service";
import { API_URL } from "../../services/url";
import SimpleReactValidator from "simple-react-validator";
import { SuccessModal } from "./SuccessModal";
export class FlakinessIndex extends Component {
  constructor(props) {
    super(props);
    this.state = {
      _isMounted : false,
      btnstatus : false,
      query : "",
      supplier: "",
      source: "",
      sample_location: "",
      weight: "",
      agg_type: "",
      weight_balance: "",
      weight_sample_testing: "",
      weight_sample_sieving: "",
      material_passing: "",
      pto_comment: "",
      total_t: "",
      total_r: "",
      total_flakiness_index: "",
      amount_status: false,
      testing_quantity: "",
      plant: "",
      date: "",
      s_r1: "",
      s_r2: "",
      s_r3: "",
      s_r4: "",
      s_r5: "",
      s_r6: "",
      s_r7: "",
      s_r8: "",
      s_r9: "",
      s_t1: "",
      s_t2: "",
      s_t3: "",
      s_t4: "",
      s_t5: "",
      s_t6: "",
      s_t7: "",
      s_t8: "",
      s_t9: ""
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.validator = new SimpleReactValidator();
  }
  componentWillMount = () => {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();

    if (dd < 10) {
      dd = "0" + dd;
    }

    if (mm < 10) {
      mm = "0" + mm;
    }

    today = mm + "/" + dd + "/" + yyyy;
    console.log("date here", today);
    this.state.date = today;
    console.log(this.state.date);

    this.setState({
      _isMounted : true
    })
  };

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
      // amount_status: true
    });
  };
  getInfo = async () => {
    try {
      let vendor_name = this.state.query
      const response = await axios.get(
        `${API_URL}master/vendor_search?VENDOR_NAME=${vendor_name}`,
        (axios.defaults.headers.common["x-access-token"] = localStorage.getItem("token"))
      );
     // console.log(response.data.User);
      //console.log(this.state.supl);
      console.log('response hwew', response)

      if (response.data.success) {
        await this.setState({
          supl: response.data.VENDOR_NAME
        })
        console.log(this.state.supl);
      }
      // this.parseJSON(this.state)
    } catch (error) {
      console.log(error);
    }


  }
  handleInputChange = (e) => {
    console.log(e.target.value)
    this.setState({
      query: e.target.value
    }, () => {
      if (this.state.query && this.state.query.length > 1) {
        if (this.state.query.length % 2 === 0) {
          if(this.state._isMounted)
          {
          this.getInfo()
          }
        }
      } else if (!this.state.query) {
      }
    })
  }
  getWebsite = () => {
    fetch("/").then(console.log(this.state));
  };
  openModal() {
    this.setState({
        visible : true
    });
}

  closeModal() {
    this.setState({
        visible : false
    });
  }
  async onSubmit(event) {
    event.preventDefault();
    console.log(this.state);
    if (this.validator.allValid()) {
      let tokenvalue = localStorage.getItem("token");
      let body = {
        supplier: this.state.query,
        source: this.state.source,
        sample_location: this.state.sample_location,
        agg_type: this.state.agg_type,
        weight_balance: this.state.weight_balance,
        weight_sample_testing: this.state.weight_sample_testing,
        pto_comment: this.state.pto_comment,
        testing_quantity: this.state.testing_quantity,
        plant: localStorage.getItem("plant_name"),
        total_t: this.state.total_t,
      total_r: this.state.total_r,
      total_flakiness_index: this.state.total_flakiness_index,
        date: this.state.date,
        s_r1: this.state.s_r1,
        s_r2: this.state.s_r2,
        s_r3: this.state.s_r3,
        s_r4: this.state.s_r4,
        s_r5: this.state.s_r5,
        s_r6: this.state.s_r6,
        s_r7: this.state.s_r7,
        s_r8: this.state.s_r8,
        s_r9: this.state.s_r9,
        s_t1: this.state.s_t1,
        s_t2: this.state.s_t2,
        s_t3: this.state.s_t3,
        s_t4: this.state.s_t4,
        s_t5: this.state.s_t5,
        s_t6: this.state.s_t6,
        s_t7: this.state.s_t7,
        s_t8: this.state.s_t8,
        s_t9: this.state.s_t9,
        type: "RF_09"
      };
      console.log("body here", body);
      this.openModal();
      this.setState({
        btnstatus : true
      })
      try {
        const response = await axios.post(
          `${API_URL}rf_form/submit`,
          body,
          (axios.defaults.headers.common["x-access-token"] = tokenvalue)
        );
        console.log(response);
        //   .then(console.log(this.state));

        if (response.data === 'success') {
          // alert(response.data);
          this.props.history.push("/ViewTestEight");
        } else {
          alert(response.data);
        }
      } catch (error) {
        console.log(error);
      }
    } else {
      this.validator.showMessages();
      // rerender to show messages for the first time
      this.forceUpdate();
    }
  }
  totalshow = e => {
    e.preventDefault();
    let total_amount_r;
    let total_amount =
      +this.state.s_t1 +
      +this.state.s_t2 +
      +this.state.s_t3 +
      +this.state.s_t4 +
      +this.state.s_t5 +
      +this.state.s_t6 +
      +this.state.s_t7 +
      +this.state.s_t8 +
      +this.state.s_t9;
    this.state.total_t = total_amount.toFixed(2);
    total_amount_r =
      +this.state.s_r1 +
      +this.state.s_r2 +
      +this.state.s_r3 +
      +this.state.s_r4 +
      +this.state.s_r5 +
      +this.state.s_r6 +
      +this.state.s_r7 +
      +this.state.s_r8 +
      +this.state.s_r9;
    this.state.total_r = total_amount_r.toFixed(2);
    let flak_ind_amount = (total_amount_r / total_amount) * 100
    this.state.total_flakiness_index = flak_ind_amount.toFixed(2)
    console.log('flak aomounth ere', flak_ind_amount)
    console.log(this.state.total_t, this.state.total_r);
    this.setState({
      amount_status: true
    });
  };
  componentWillUnmount () {
    this.state._isMounted = false
  }
  render() {
     // let showmodal;
     let sbmtbtn, btnmsg;
     if(this.state.visible)
     {
       // showmodal = (
       //   <div>
       //     <SuccessModal  
       //          visible={this.state.visible}
       //               width={this.props.width}
       //               height={this.props.height}
       //               effect={this.props.effect}
       //               onClickAway={() => this.closeModal()} />
       //   </div>
       // )
     }
     if(this.state.btnstatus)
     {
       sbmtbtn = (
         <button
         class="btn btn-primary"
         onClick={this.onSubmit.bind(this)}
         disabled
       >
         Submit
       </button>
       )
       btnmsg = (
         <div>
         <hr />
         <p>Your Form Has Already Been Submitted.Please Don't Click SUMBIT Button AGAIN!!</p>
         </div>
       )
     }
     else
     {
       sbmtbtn=(
         <button
         class="btn btn-primary"
         onClick={this.onSubmit.bind(this)}
       >
         Submit
       </button>
       )
     }

    let buttontext, display_amount_t, display_amount_r, flak_ind_amount;
    if (this.props.match.url == "/FLakinessIndex") {
      buttontext = (
        <span
          style={{
            fontWeight: "bold",
            fontStyle: "italic",
            color: "forestgreen"
          }}
        >
          Flakiness Index
        </span>
      );
    }

    //CONDITION FOR SHOW TOTAL ON BTN
    if (this.state.amount_status === true) {
      display_amount_t = <div>{this.state.total_t}</div>;
      display_amount_r = <div>{this.state.total_r}</div>;
      if(this.state.total_t === 0 || this.state.total_r === 0)
      {
        flak_ind_amount = (
          <div>
            Your Calculation Is Wrong
          </div>
        )
      }
      else
      {
      flak_ind_amount = <div>{this.state.total_flakiness_index}</div>
      }
    } else {
      display_amount_t = (
        <div>
          {/* <button
            type="button"
            className="btn btn-info"
            onClick={this.totalshow.bind(this)}
          >
            Show Total
          </button> */}
          Please Fill The Above Field
        </div>
      );
      display_amount_r = (
        <div>
          {/* <button
            type="button"
            className="btn btn-info"
            onClick={this.totalshow.bind(this)}
          >
            Show Total
          </button> */}
          Please Fill the Above Field
        </div>
      );
      flak_ind_amount = (
        <div>Please Do The Above Calculation First</div>
      )
    }


    //CHANGE FUNC BELOW

    // let display_total_amount;
    // if (this.state.amount_status == true) {
    //   let total_amount_t =
    //   +this.state.s_t1 +
    //   +this.state.s_t2 +
    //   +this.state.s_t3 +
    //   +this.state.s_t4 +
    //   +this.state.s_t5 +
    //   +this.state.s_t6 +
