import React, { Component } from "react";

import "./App.css";
import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import Home from "./components/Home/Home";
import Sidebar from "./components/Sidebar/Sidebar";

import Signin from "./components/Login/AddLogin";
import Register from "./components/Login/Register";
import Navigation from "./components/Navigation";

import Admin from "./components/Admin/Admin";
import ViewAdmin from "./components/Admin/ViewAdmin";
import EditAdmin from "./components/Admin/EditAdmin";

import Employee from "./components/Employee/Employee";
import ViewEmployee from "./components/Employee/ViewEmployee";
import EmployeAllocation from "./components/EmployeAllocation/EmployeAllocation";
import ViewEmployeAllocation from "./components/EmployeAllocation/ViewEmployeAllocation";
import Profile from "./components/Profile/Profile";
import ViewProfile from "./components/Profile/ViewProfile";

import Zone from "./components/Zone/Zone";
import ViewZone from "./components/Zone/ViewZone";
import EditZone from "./components/Zone/EditZone";

import AddLocation from "./components/Location/AddLocation";
import ViewLocations from "./components/Location/ViewLocations";

import { Route, Redirect } from "react-router-dom";
import EditLocation from "./components/Location/EditLocation";

import EditEmploye from "./components/Employee/EditEmploye";

import EditEmplalloc from "./components/EmployeAllocation/EditEmplalloc";

import { ZoneLoc } from "./components/Zone/ZoneLoc";

import { Area } from "./components/Area/Area";
import { ViewArea } from "./components/Area/ViewArea";
import { ViewPlant } from "./components/Plant/ViewPlant";
import { Plant } from "./components/Plant/Plant";
import PlantScale from "./components/Plant/PlantScale";
import { PlantAllocate } from "./components/Plant/PlantAllocate";
import { AreaAllocate } from "./components/Area/AreaAllocate";
import { ZoneAllocate } from "./components/Zone/ZoneAllocate";

import { ViewTestOne } from "./components/Test/ViewTestOne";

import { ViewTestTwo } from "./components/Test/ViewTestTwo";

import { ViewTestThree } from "./components/Test/ViewTestThree";
import { Testfour } from "./components/Test/Testfour";
import { ViewTestFour } from "./components/Test/ViewTestFour";
import { Testfive } from "./components/Test/Testfive";
import { ViewTestFive } from "./components/Test/ViewTestFive";
import { Testsix } from "./components/Test/Testsix";
import { ViewTestSix } from "./components/Test/ViewTestSix";
import { Testseven } from "./components/Test/Testseven";
import { ViewTestSeven } from "./components/Test/ViewTestSeven";
import { Testeight } from "./components/Test/Testeight";
import { ViewTestEight } from "./components/Test/ViewTestEight";
import { UserList } from "./components/Employee/UserList";
import { ViewAssets } from "./components/Assets/ViewAssets";
import { AssetType } from "./components/Assets/AssetType";
import { AssetPlant } from "./components/Assets/AssetPlant";
import { ViewAssetPlant } from "./components/Assets/ViewAssetPlant";

import { ComingSoon } from "./components/Test/ComingSoon";
import { AT1 } from "./components/Test/AT1";
import RegionAllocated from "./components/Zone/AllocatedZone";
import AllocatedPlant from "./components/Plant/AllocatedPlant";
import AllocatedArea from "./components/Area/AllocatedArea";
// import { AT2 } from "./components/Test/AT2";
// import { AT3 } from "./components/Test/AT3";
import PrivateRoute from "./auth/privateRoute";
import PTORoute from "./auth/ptoRoute";


import { Add } from "./RF-Route/Add";
import { Verify } from "./RF-Route/verify";
import { View } from "./RF-Route/View";
import { DetailView } from "./RF-Route/DetailView";
import { ATAdd } from "./AT-Route/Add";
import { ViewAT } from "./AT-Route/View";
import { DetailAT } from "./AT-Route/DetailView";
import { VerifyAT } from "./AT-Route/Verify";
import { Asset } from "./components/Test/Asset";
import { BOMView } from "./components/Master/BOMView";
import { Verifiedatroute } from "./AT-Route/verifiedatroute";
import { Verification } from "./components/Test/Verification";
import BatchView from "./components/reports/BatchView";
import TwentyEightDays from "./components/CubeCasting/TwentyEightDays";
import SevenDays from "./components/CubeCasting/SevenDays";
import Table7 from "./components/CubeCasting/Table7";
import Complete from "./components/CubeCasting/Complete";
import { VIEW7 } from "./components/CubeCasting/VIEW7";
import VApproved from "./components/CubeCasting/VApproved";
import VRejected from "./components/CubeCasting/VRejected";

class App extends Component {
  constructor() {
    super();
    this.state = {
      route: "signin",
      isSignedIn: false
    };
  }

  onRouteChange = route => {
    if (route === "signout") {
      this.setState({ isSignedIn: false });
    } else if (route === "home") {
      this.setState({ isSignedIn: true });
    }
    this.setState({ route: route });
  };
  componentWillMount() {
    var that = this;
    setTimeout(function () {
      //that.show();
    }, that.props.wait);
  }

  render() {
    const { isSignedIn, route } = this.state;

    return (
      <div>
        {localStorage.getItem("token") ? (
          <div>
             <Header /> 
            <div isSignedIn={isSignedIn} onRouteChange={this.onRouteChange} />
            <Header />
            <Sidebar />
            <Route path={process.env.PUBLIC_URL + "/"} exact component={Home} />

            {/* <Route
              path={process.env.PUBLIC_URL + "/Admin"}
              exact={true}
              component={Admin}
            />
            <Route
              path={process.env.PUBLIC_URL + "/ViewAdmin"}
              exact={true}
              component={ViewAdmin}
            />
            <Route
              exact
              path={process.env.PUBLIC_URL + "/EditAdmin/:id"}
              component={EditAdmin}
            />

            <PrivateRoute
              path={process.env.PUBLIC_URL + "/Employee"}
              exact={true}
              component={Employee}
            />*/}
            <Route
              path={process.env.PUBLIC_URL + "/Profile"}
              exact={true}
              component={Profile}
            />
            <Route
              path={process.env.PUBLIC_URL + "/ViewProfile"}
              exact={true}
              component={ViewProfile}
            />
            <Route
              path={process.env.PUBLIC_URL + "/Testfour"}
              exact={true}
              component={Testfour}
            />
            <Route
              path={process.env.PUBLIC_URL + "/Testfive"}
              exact={true}
              component={Testfive}
            />
            <Route
              path={process.env.PUBLIC_URL + "/Testsix"}
              exact={true}
              component={Testsix}
            />

            <Route
              path={process.env.PUBLIC_URL + "/Testseven"}
              exact={true}
              component={Testseven}
            />
            <Route
              path={process.env.PUBLIC_URL + "/PlantScale"}
              exact={true}
              component={PlantScale}
            />

            <Route
              path={process.env.PUBLIC_URL + "/BatchView"}
              exact={true}
              component={BatchView}
            />
            <Route
              path={process.env.PUBLIC_URL + "/Table7"}
              exact={true}
              component={Table7}
            />
            <Route
              path={process.env.PUBLIC_URL + "/SevenDays"}
              exact={true}
              component={SevenDays}
            />
            <Route
              path={process.env.PUBLIC_URL + "/TwentyEightDays"}
              exact={true}
              component={TwentyEightDays}
            />
            <Route
              path={process.env.PUBLIC_URL + "/Complete"}
              exact={true}
              component={Complete}
            />

            <Route
              path={process.env.PUBLIC_URL + "/VIEW7/:id"}
              exact={true}
              component={VIEW7}
            />

            <Route
              path={process.env.PUBLIC_URL + "/VApproved"}
              exact={true}
              component={VApproved}
            />

            <Route
              path={process.env.PUBLIC_URL + "/VRejected"}
              exact={true}
              component={VRejected}
            />

            {/* <Route
              path={process.env.PUBLIC_URL + "/UserList"}
              exact={true}
              component={UserList}
            /> */}
            <Route
              path={process.env.PUBLIC_URL + "/ViewAssets"}
              exact={true}
              component={ViewAssets}
            />
            <Route
              path={process.env.PUBLIC_URL + "/AssetType"}
              exact={true}
              component={AssetType}
            />
            <Route
              path={process.env.PUBLIC_URL + "/AssetPlant"}
              exact={true}
              component={AssetPlant}
            />
            <Route
              path={process.env.PUBLIC_URL + "/ViewAssetPlant"}
              exact={true}
              component={ViewAssetPlant}
            />

            <Route
              path={process.env.PUBLIC_URL + "/ComingSoon"}
              exact={true}
              component={ComingSoon}
            />


            <Route
              path={process.env.PUBLIC_URL + "/Asset"}
              exact={true}
              component={Asset}
            />

            <Route
              path={process.env.PUBLIC_URL + "/BOMView"}
              exact={true}
              component={BOMView}
            />

            />
            <Route
              path={process.env.PUBLIC_URL + "/Verification"}
              exact={true}
              component={Verification}
            />

            />
            <Add />
            <View />
            <ATAdd />
            <ViewAT />
            <Verify />
            <DetailView />
            <DetailAT />
            <VerifyAT />
            <Verifiedatroute />
          </div>
        ) : route === "signin" ? (
          <Signin onRouteChange={this.onRouteChange} wait={1} />
        ) : (
              window.location.reload()
            ) 
}

        <Footer />
      </div>
    );
  }
}

export default App;
