import React, { Component } from 'react';
import './EmployeAllocation.css';
import {Link} from 'react-router-dom';
import axios from 'axios'
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';
class ViewEmployeAllocation extends Component {
  constructor () {
    super() 
    this.state = {
      userdata : [],
      detailsdata : [],
      currentPage: 1,
      todosPerPage: 10
    }
    this.handlepagenumber = this.handlepagenumber.bind(this);
    this.handleCheck = this.handleCheck.bind(this);
    this.detailCheck = this.detailCheck.bind(this);
}
handlepagenumber(event) {
  this.setState({
    currentPage: Number(event.target.id)
  });
}
handleCheck(todo) {
  console.log(todo.emp_loc_id);
  let sitemeet = todo.emp_loc_id;
  if(window.confirm('Are You Sure You Want To Delete The Allocated Supervisor?'))
  {
    axios.get(`${API_URL}emp_loc/delete?id=${sitemeet}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
    .then(function(response){
      console.log(response.data)
         if(response.data.success===true){
           alert(response.data.msg)
           this.handleClick()
    }
    else{
      console.log(response.data.msg);
  }
  }.bind(this))
  .catch(error => {
   console.log(error.response);
 })
  }

 }
 detailCheck(todo) {
  console.log(todo.zone_id);
  let sitemeet = todo.zone_id;
  fetch(`${API_URL}zone/detail?id=${sitemeet}`,{
             method : 'GET',
             headers : {
               'Authorization': 'Bearer ' + this.state.token,
               "Content-Type" : "application/json"
           }
           })
           .then((res) => {
            res.json().then((response) => { 
              console.log(response.zonedata)
              this.setState({ detailsdata:response.zonedata})
              // this.parseJSON(this.state)
            })
            .catch(error => {
              console.log(error.response);
            })
          }

        )
 }


componentDidMount ()
{
  this.handleClick();
}
handleClick () {
  axios.get(`${API_URL}emp_loc/view`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
  .then((response) => { 
    console.log(response)
        //console.log(response)
        if(response.data.success)
        {
        this.setState({ userdata:response.data.emp_loc_data})
        }
        // this.parseJSON(this.state)
      })
      .catch(error => {
        console.log(error.response);
      })

  }
    render() {
      const { todos, currentPage, todosPerPage } = this.state;

      let renderTodos

      // Logic for displaying todos
      const indexOfLastTodo = currentPage * todosPerPage;
      const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
      const currentTodos = this.state.userdata.slice(indexOfFirstTodo, indexOfLastTodo);



      let zonedatatable;
      let serial = 0
      if(this.state.userdata.length === 0) 
      {
        renderTodos = (
          <div>
          <br />
              <center><h4><strong>There is NO Supervisor Allocated Yet.</strong></h4></center>
          </div>
        )
      }
      else {
        renderTodos = currentTodos.map((todo, index) => {
          return (

               <tr key = {index}>
               {/* <th scope="row">{++serial}</th> */}
                  <td>{todo.employe_name || "NO DATA"}</td>
                  <td>{todo.employe_mobile1 || "NO DATA"}</td>
                  <td>{todo.employe_mobile2 || "NO DATA"}</td>
                  <td>{todo.employe_email || "NO DATA"}</td>
                  <td>{todo.zone_name || "NO DATA"}</td>
                  <td>
                  <Link to ={process.env.PUBLIC_URL +`/EditSupAlloc/${todo.emp_loc_id}`}>
  <span class="tooltip-toggle" aria-label="Edit" tabindex="0">
    <button className="btn btn-warning custom-edit-btn btn-sm"><i class="fa fa-pencil" aria-hidden="true"></i></button>
    </span>
    </Link>
    <span class="tooltip-toggle" aria-label="Suspend" tabindex="0">
       <button  className="btn btn-danger custom-edit-btn btn-sm" onClick={this.handleCheck.bind(this, todo)}><i class="fa fa-trash-o" aria-hidden="true"></i></button>
      </span>
                  </td>
                </tr>

          )
          });
      }
        // Logic for displaying page numbers
const pageNumbers = [];
for (let i = 1; i <= Math.ceil(this.state.userdata.length / todosPerPage); i++) {
